//===========================================// ANIMAL THERAPY PROGRAMS-JAVASCRIPT //===========================================document.addEventListener('DOMContentLoaded',function(){//===========================================// ACCORDION FUNCTIONALITY //===========================================const accordionHeaders=document.querySelectorAll('.accordion-header');accordionHeaders.forEach(header=>{header.addEventListener('click',function(){const accordionItem=this.parentElement;const isActive=accordionItem.classList.contains('active');// Close all accordion items document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.remove('active');});// Open clicked item if it wasn't active if(!isActive){accordionItem.classList.add('active');}});});//===========================================// FAQ ACCORDION FUNCTIONALITY //===========================================const faqQuestions=document.querySelectorAll('.faq-question');faqQuestions.forEach(question=>{question.addEventListener('click',function(){const faqItem=this.parentElement;const isActive=faqItem.classList.contains('active');// Close all FAQ items document.querySelectorAll('.faq-item').forEach(item=>{item.classList.remove('active');});// Open clicked item if it wasn't active if(!isActive){faqItem.classList.add('active');}});});//===========================================// SMOOTH SCROLLING FOR ANCHOR LINKS //===========================================document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){const href=this.getAttribute('href');// Skip if href is just "#" or empty if(href==='#' || !href){e.preventDefault();return;}const target=document.querySelector(href);if(target){e.preventDefault();const headerOffset=80;const elementPosition=target.getBoundingClientRect().top;const offsetPosition=elementPosition+window.pageYOffset-headerOffset;window.scrollTo({top: offsetPosition,behavior: 'smooth'});}});});//===========================================// COUNTER ANIMATION FOR STATISTICS //===========================================function animateCounter(element,target,duration=2000){const start=0;const increment=target /(duration / 16);// 60 FPS let current=start;const timer=setInterval(()=>{current+=increment;if(current >=target){element.textContent=target+'%';clearInterval(timer);}else{element.textContent=Math.floor(current)+'%';}},16);}// Intersection Observer for counter animation const statCards=document.querySelectorAll('.stat-card h3');const observerOptions={threshold: 0.5,rootMargin: '0px 0px-100px 0px'};const statObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting && !entry.target.dataset.animated){const targetValue=parseInt(entry.target.textContent);animateCounter(entry.target,targetValue);entry.target.dataset.animated='true';}});},observerOptions);statCards.forEach(card=>{statObserver.observe(card);});//===========================================// SCROLL-TRIGGERED ANIMATIONS //===========================================const animatedElements=document.querySelectorAll('[data-aos]');const scrollObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('aos-animate');}});},{threshold: 0.1,rootMargin: '0px 0px-50px 0px'});animatedElements.forEach(element=>{scrollObserver.observe(element);});//===========================================// ANIMAL CARD INTERACTION //===========================================const animalCards=document.querySelectorAll('.animal-card');animalCards.forEach(card=>{card.addEventListener('mouseenter',function(){this.style.zIndex='10';});card.addEventListener('mouseleave',function(){this.style.zIndex='1';});});//===========================================// BENEFIT CARDS STAGGER ANIMATION //===========================================const benefitCards=document.querySelectorAll('.benefit-card');const benefitObserver=new IntersectionObserver((entries)=>{entries.forEach((entry,index)=>{if(entry.isIntersecting){setTimeout(()=>{entry.target.style.opacity='1';entry.target.style.transform='translateY(0)';},index * 100);}});},{threshold: 0.2});benefitCards.forEach(card=>{card.style.opacity='0';card.style.transform='translateY(30px)';card.style.transition='all 0.6s ease';benefitObserver.observe(card);});//===========================================// STORY CARDS HOVER EFFECT //===========================================const storyCards=document.querySelectorAll('.story-card');storyCards.forEach(card=>{card.addEventListener('mouseenter',function(){const icon=this.querySelector('.story-header i');if(icon){icon.style.transform='scale(1.2)rotate(10deg)';}});card.addEventListener('mouseleave',function(){const icon=this.querySelector('.story-header i');if(icon){icon.style.transform='scale(1)rotate(0deg)';}});});//===========================================// PARALLAX EFFECT FOR HERO DECORATION //===========================================const heroDecoration=document.querySelector('.hero-decoration');if(heroDecoration){window.addEventListener('scroll',()=>{const scrolled=window.pageYOffset;const rate=scrolled * 0.3;heroDecoration.style.transform=`translateY(${rate}px)`;});}//===========================================// INVOLVEMENT CARD INTERACTION //===========================================const involvementCards=document.querySelectorAll('.involvement-card');involvementCards.forEach(card=>{const icon=card.querySelector('.involvement-icon');card.addEventListener('mouseenter',function(){if(icon){icon.style.transform='scale(1.1)rotate(5deg)';icon.style.transition='all 0.3s ease';}});card.addEventListener('mouseleave',function(){if(icon){icon.style.transform='scale(1)rotate(0deg)';}});});//===========================================// ORGANIZATION CARD LINK HOVER //===========================================const orgCards=document.querySelectorAll('.org-card');orgCards.forEach(card=>{card.addEventListener('mouseenter',function(){const link=this.querySelector('.org-link');if(link){link.style.textDecoration='underline';}});card.addEventListener('mouseleave',function(){const link=this.querySelector('.org-link');if(link){link.style.textDecoration='none';}});});//===========================================// SCIENCE CARD ROTATION EFFECT //===========================================const scienceCards=document.querySelectorAll('.science-card');scienceCards.forEach(card=>{card.addEventListener('mousemove',function(e){const rect=this.getBoundingClientRect();const x=e.clientX-rect.left;const y=e.clientY-rect.top;const centerX=rect.width / 2;const centerY=rect.height / 2;const rotateX=(y-centerY)/ 20;const rotateY=(centerX-x)/ 20;this.style.transform=`perspective(1000px)rotateX(${rotateX}deg)rotateY(${rotateY}deg)translateY(-10px)`;});card.addEventListener('mouseleave',function(){this.style.transform='perspective(1000px)rotateX(0)rotateY(0)translateY(0)';});});//===========================================// CTA BUTTON RIPPLE EFFECT //===========================================const buttons=document.querySelectorAll('.btn-primary,.btn-secondary');buttons.forEach(button=>{button.addEventListener('click',function(e){const rect=this.getBoundingClientRect();const x=e.clientX-rect.left;const y=e.clientY-rect.top;const ripple=document.createElement('span');ripple.style.position='absolute';ripple.style.left=x+'px';ripple.style.top=y+'px';ripple.style.width='0';ripple.style.height='0';ripple.style.borderRadius='50%';ripple.style.background='rgba(255,255,255,0.5)';ripple.style.transform='translate(-50%,-50%)';ripple.style.animation='ripple 0.6s ease-out';this.style.position='relative';this.style.overflow='hidden';this.appendChild(ripple);setTimeout(()=>{ripple.remove();},600);});});// Add ripple animation to stylesheet const style=document.createElement('style');style.textContent=` @keyframes ripple{to{width: 300px;height: 300px;opacity: 0;}}`;document.head.appendChild(style);//===========================================// LAZY LOADING FOR IMAGES(IF ANY ARE ADDED)//===========================================if('IntersectionObserver' in window){const imageObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;if(img.dataset.src){img.src=img.dataset.src;img.classList.add('loaded');observer.unobserve(img);}}});});document.querySelectorAll('img[data-src]').forEach(img=>{imageObserver.observe(img);});}//===========================================// ACCESSIBILITY ENHANCEMENTS //===========================================// Add keyboard navigation for accordion accordionHeaders.forEach(header=>{header.setAttribute('tabindex','0');header.setAttribute('role','button');header.setAttribute('aria-expanded','false');header.addEventListener('keydown',function(e){if(e.key==='Enter' || e.key===' '){e.preventDefault();this.click();const isExpanded=this.parentElement.classList.contains('active');this.setAttribute('aria-expanded',isExpanded);}});});// Add keyboard navigation for FAQ faqQuestions.forEach(question=>{question.setAttribute('tabindex','0');question.setAttribute('role','button');question.setAttribute('aria-expanded','false');question.addEventListener('keydown',function(e){if(e.key==='Enter' || e.key===' '){e.preventDefault();this.click();const isExpanded=this.parentElement.classList.contains('active');this.setAttribute('aria-expanded',isExpanded);}});});//===========================================// PRINT FUNCTIONALITY //===========================================window.addEventListener('beforeprint',()=>{// Expand all accordions for printing document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.add('active');});document.querySelectorAll('.faq-item').forEach(item=>{item.classList.add('active');});});window.addEventListener('afterprint',()=>{// Collapse all accordions after printing document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.remove('active');});document.querySelectorAll('.faq-item').forEach(item=>{item.classList.remove('active');});});//===========================================// SCROLL TO TOP BUTTON //===========================================const scrollToTopBtn=document.createElement('button');scrollToTopBtn.innerHTML='<i class="fas fa-arrow-up"></i>';scrollToTopBtn.className='scroll-to-top';scrollToTopBtn.setAttribute('aria-label','Scroll to top');scrollToTopBtn.style.cssText=` position: fixed;bottom: 30px;right: 30px;width: 50px;height: 50px;border-radius: 50%;background: linear-gradient(135deg,#667eea,#764ba2);color: white;border: none;font-size: 1.2rem;cursor: pointer;opacity: 0;visibility: hidden;transition: all 0.3s ease;z-index: 1000;box-shadow: 0 5px 20px rgba(102,126,234,0.3);`;document.body.appendChild(scrollToTopBtn);window.addEventListener('scroll',()=>{if(window.pageYOffset > 300){scrollToTopBtn.style.opacity='1';scrollToTopBtn.style.visibility='visible';}else{scrollToTopBtn.style.opacity='0';scrollToTopBtn.style.visibility='hidden';}});scrollToTopBtn.addEventListener('click',()=>{window.scrollTo({top: 0,behavior: 'smooth'});});//===========================================// LOG PAGE VIEW(FOR ANALYTICS)//===========================================console.log('Animal Therapy Programs page loaded successfully');// Track how long users spend on page(optional)let pageLoadTime=new Date().getTime();window.addEventListener('beforeunload',()=>{let timeSpent=(new Date().getTime()-pageLoadTime)/ 1000;console.log(`User spent ${Math.round(timeSpent)}seconds on this page`);});});