//==========INITIALIZATION==========document.addEventListener('DOMContentLoaded',function(){// Initialize all components initEmergencyAccordions();initAssessmentChecklist();initContactDisplay();initHeroBackground();});function initEmergencyAccordions(){const emergencyTypes=document.querySelectorAll('.emergency-type');emergencyTypes.forEach(type=>{const header=type.querySelector('.emergency-header');const content=type.querySelector('.emergency-content');// Initially collapse content content.style.maxHeight='0';content.style.overflow='hidden';content.style.transition='max-height 0.3s ease';header.addEventListener('click',function(){const isExpanded=content.style.maxHeight !=='0px';if(isExpanded){content.style.maxHeight='0';header.classList.remove('expanded');}else{content.style.maxHeight=content.scrollHeight+'px';header.classList.add('expanded');}});// Add visual indicator header.style.cursor='pointer';header.addEventListener('mouseenter',function(){this.style.backgroundColor='rgba(220,38,38,0.1)';});header.addEventListener('mouseleave',function(){this.style.backgroundColor='';});});}function initAssessmentChecklist(){// Create a dynamic checklist for emergency assessment const assessmentSection=document.querySelector('.assessment-section');if(assessmentSection){const checklistContainer=document.createElement('div');checklistContainer.className='assessment-checklist';checklistContainer.innerHTML=` <h3>Emergency Assessment Checklist</h3> <div class="checklist-items"> <label class="checklist-item"> <input type="checkbox" id="safety-check"> <span class="checkmark"></span> Ensured personal safety and scene security </label> <label class="checklist-item"> <input type="checkbox" id="observe-check"> <span class="checkmark"></span> Observed animal from safe distance </label> <label class="checklist-item"> <input type="checkbox" id="contact-check"> <span class="checkmark"></span> Contacted professional wildlife help </label> <label class="checklist-item"> <input type="checkbox" id="document-check"> <span class="checkmark"></span> Documented location and situation details </label> </div> `;assessmentSection.appendChild(checklistContainer);// Add checklist styling const style=document.createElement('style');style.textContent=` .assessment-checklist{background: #fef2f2;border: 2px solid #fecaca;border-radius: 12px;padding: 2rem;margin-top: 3rem;max-width: 600px;margin-left: auto;margin-right: auto;}.assessment-checklist h3{color: #dc2626;text-align: center;margin-bottom: 1.5rem;font-size: 1.5rem;}.checklist-items{display: flex;flex-direction: column;gap: 1rem;}.checklist-item{display: flex;align-items: center;cursor: pointer;padding: 0.5rem;border-radius: 8px;transition: background-color 0.2s;}.checklist-item:hover{background-color: rgba(220,38,38,0.1);}.checklist-item input[type="checkbox"]{display: none;}.checkmark{width: 20px;height: 20px;border: 2px solid #dc2626;border-radius: 4px;margin-right: 1rem;position: relative;transition: all 0.2s;}.checklist-item input:checked+.checkmark{background-color: #dc2626;}.checklist-item input:checked+.checkmark::after{content: 'âœ“';position: absolute;top:-2px;left: 2px;color: white;font-weight: bold;}`;document.head.appendChild(style);}}function initContactDisplay(){const resourceCards=document.querySelectorAll('.resource-card');resourceCards.forEach(card=>{card.addEventListener('click',function(){const cardType=this.querySelector('h3').textContent;// Show relevant contact information based on card type let contactInfo='';switch(cardType){case 'Wildlife Rehabilitation Centers': contactInfo='Search for licensed wildlife rehabilitation centers in your area through local wildlife agencies or organizations like the International Wildlife Rehabilitation Council.';break;case 'Emergency Hotlines': contactInfo='Contact your local animal control,wildlife agency,or veterinary emergency services. In the US,you can also call the ASPCA Animal Poison Control Center at(888)426-4435.';break;case 'Wildlife Organizations': contactInfo='Organizations like the Humane Society,Wildlife Rescue League,or local conservation groups can provide guidance and referrals.';break;case 'Online Resources': contactInfo='Visit websites like wildlife.org,defenders.org,or your state wildlife agency website for emergency information and local referrals.';break;}if(contactInfo){showContactModal(cardType,contactInfo);}});});}function showContactModal(title,info){// Create modal if it doesn't exist let modal=document.getElementById('contact-modal');if(!modal){modal=document.createElement('div');modal.id='contact-modal';modal.innerHTML=` <div class="modal-overlay"> <div class="modal-content"> <div class="modal-header"> <h3 id="modal-title"></h3> <button class="modal-close">&times;</button> </div> <div class="modal-body"> <p id="modal-info"></p> </div> </div> </div> `;document.body.appendChild(modal);// Add modal styling const style=document.createElement('style');style.textContent=` .modal-overlay{position: fixed;top: 0;left: 0;width: 100%;height: 100%;background: rgba(0,0,0,0.7);display: flex;align-items: center;justify-content: center;z-index: 1000;}.modal-content{background: white;border-radius: 12px;padding: 2rem;max-width: 500px;width: 90%;max-height: 80vh;overflow-y: auto;}.modal-header{display: flex;justify-content: space-between;align-items: center;margin-bottom: 1rem;}.modal-header h3{margin: 0;color: #dc2626;}.modal-close{background: none;border: none;font-size: 2rem;cursor: pointer;color: #6b7280;}.modal-body p{line-height: 1.6;color: #374151;}`;document.head.appendChild(style);// Close modal functionality modal.addEventListener('click',function(e){if(e.target===modal || e.target.classList.contains('modal-close')){modal.style.display='none';}});}// Update modal content document.getElementById('modal-title').textContent=title;document.getElementById('modal-info').textContent=info;modal.style.display='flex';}function initHeroBackground(){const heroBg=document.getElementById('heroBg');let lastScrollY=window.scrollY;window.addEventListener('scroll',()=>{const currentScrollY=window.scrollY;const deltaY=currentScrollY-lastScrollY;// Subtle parallax effect const translateY=currentScrollY * 0.5;heroBg.style.transform=`translateY(${translateY}px)scale(${1+currentScrollY * 0.0002})`;lastScrollY=currentScrollY;});}function isInViewport(element){const rect=element.getBoundingClientRect();return(rect.top >=0 && rect.left >=0 && rect.bottom <=(window.innerHeight || document.documentElement.clientHeight)&& rect.right <=(window.innerWidth || document.documentElement.clientWidth));}//==========RESPONSIVE BEHAVIOR==========window.addEventListener('resize',function(){// Reinitialize components if needed for responsive design initEmergencyAccordions();});//==========ACCESSIBILITY==========document.addEventListener('keydown',function(e){// Add keyboard navigation for interactive elements if(e.key==='Enter' || e.key===' '){const focusedElement=document.activeElement;if(focusedElement.classList.contains('emergency-header')){focusedElement.click();e.preventDefault();}}// Close modal with Escape key if(e.key==='Escape'){const modal=document.getElementById('contact-modal');if(modal && modal.style.display==='flex'){modal.style.display='none';}}});// // <!--Theme toggle script--> // // Initialize AOS // AOS.init({// duration: 800,// once: true,//});// // Theme toggle functionality // document.addEventListener('DOMContentLoaded',function(){// const themeToggle=document.getElementById('themeToggle');// const html=document.documentElement;// // Check for saved theme or default to light // const savedTheme=localStorage.getItem('theme')|| 'light';// html.setAttribute('data-theme',savedTheme);// updateToggle(savedTheme);// // Toggle theme on click // themeToggle.addEventListener('click',function(){// const currentTheme=html.getAttribute('data-theme');// const newTheme=currentTheme==='light' ? 'dark' : 'light';// html.setAttribute('data-theme',newTheme);// localStorage.setItem('theme',newTheme);// updateToggle(newTheme);//});// // Update toggle position based on theme // function updateToggle(theme){// const slider=document.querySelector('.theme-slider');// if(theme==='dark'){// slider.style.transform='translateX(26px)';//}else{// slider.style.transform='translateX(0)';//}//}//});//});