//==========GLOBAL VARIABLES==========let animalsData=[];let filteredAnimals=[];let currentFilter='all';//==========INITIALIZATION==========document.addEventListener('DOMContentLoaded',function(){// Initialize all components initParallaxEffect();initSearchFunctionality();initModalFunctionality();loadAnimalsData();});//==========PARALLAX EFFECTS==========function initParallaxEffect(){document.addEventListener('mousemove',function(e){const heroBg=document.getElementById('heroBg');if(heroBg){const x=(window.innerWidth-e.pageX * 5)/ 100;const y=(window.innerHeight-e.pageY * 5)/ 100;heroBg.style.transform=`translateX(${x}px)translateY(${y}px)`;}});}//==========DATA LOADING==========async function loadAnimalsData(){try{const response=await fetch('../assets/data/endangered-animals.json');if(!response.ok){throw new Error(`HTTP error! status: ${response.status}`);}animalsData=await response.json();filteredAnimals=[...animalsData];displayAnimals(filteredAnimals);console.log(`Loaded ${animalsData.length}endangered animal species`);}catch(error){console.error('Error loading animals data:',error);showNoResultsMessage('Failed to load animal data. Please try again later.');}}//==========DISPLAY FUNCTIONS==========function displayAnimals(animals){const grid=document.getElementById('animalsGrid');const noResults=document.getElementById('noResults');if(!grid)return;if(animals.length===0){grid.innerHTML='';if(noResults)noResults.style.display='block';return;}if(noResults)noResults.style.display='none';grid.innerHTML=animals.map(animal=> ` <div class="animal-card" onclick="openModal(${animal.id})"> <div class="card-image"> <img src="${animal.image}" alt="${animal.name}" class="animal-image" onerror="this.src='https://via.placeholder.com/400x200?text=No+Image'"> <span class="status-badge-overlay status-${animal.status}">${animal.status}</span> </div> <div class="card-content"> <div class="card-header"> <h3 class="animal-name">${animal.name}</h3> <p class="scientific-name">${animal.scientificName}</p> </div> <div class="card-info"> <div class="info-item"> <span class="info-icon">🏠</span> <span class="info-text">&nbsp;${animal.habitat}</span> </div> </div> <div class="card-tags"> ${animal.threats.slice(0,2).map(threat=> `<span class="threat-tag">${threat}</span>`).join('')}${animal.threats.length > 2 ? `<span class="threat-tag">+${animal.threats.length-2}</span>` : ''}</div> <p class="animal-description">${truncateText(animal.description,80)}</p> <div class="card-footer"> <span class="learn-more">Learn More →</span> </div> </div> </div> `).join('');}//==========SEARCH FUNCTIONALITY==========function initSearchFunctionality(){const searchInput=document.getElementById('searchInput');if(!searchInput)return;// Debounced input event for real-time search searchInput.addEventListener('input',debounce(searchAnimals,300));// Enter key support searchInput.addEventListener('keypress',function(e){if(e.key==='Enter'){searchAnimals();}});}function searchAnimals(){if(!animalsData || animalsData.length===0)return;const query=document.getElementById('searchInput')?.value.toLowerCase().trim()|| '';if(!query){// No search query-apply current filter only filteredAnimals=currentFilter==='all' ? [...animalsData] : animalsData.filter(animal=> animal.status===currentFilter);}else{// Search across multiple fields const searchResults=animalsData.filter(animal=> animal.name.toLowerCase().includes(query)|| animal.scientificName.toLowerCase().includes(query)|| animal.description.toLowerCase().includes(query)|| animal.habitat.toLowerCase().includes(query)|| animal.locations.some(location=> location.toLowerCase().includes(query))|| animal.threats.some(threat=> threat.toLowerCase().includes(query)));// Apply current filter to search results filteredAnimals=currentFilter==='all' ? searchResults : searchResults.filter(animal=> animal.status===currentFilter);}displayAnimals(filteredAnimals);}// Make searchAnimals global for HTML onclick handlers window.searchAnimals=searchAnimals;//==========FILTERING SYSTEM==========function filterAnimals(status,event){if(!animalsData || animalsData.length===0)return;currentFilter=status;// Update active filter button styling document.querySelectorAll('.filter-btn').forEach(btn=> btn.classList.remove('active'));event.target.classList.add('active');const query=document.getElementById('searchInput')?.value.toLowerCase().trim()|| '';if(status==='all'){// Show all animals,apply search if present filteredAnimals=query ? animalsData.filter(animal=> animal.name.toLowerCase().includes(query)|| animal.scientificName.toLowerCase().includes(query)|| animal.description.toLowerCase().includes(query)|| animal.habitat.toLowerCase().includes(query)|| animal.locations.some(location=> location.toLowerCase().includes(query))|| animal.threats.some(threat=> threat.toLowerCase().includes(query))): [...animalsData];}else{// Filter by status first,then apply search const statusFiltered=animalsData.filter(animal=> animal.status===status);filteredAnimals=query ? statusFiltered.filter(animal=> animal.name.toLowerCase().includes(query)|| animal.scientificName.toLowerCase().includes(query)|| animal.description.toLowerCase().includes(query)|| animal.habitat.toLowerCase().includes(query)|| animal.locations.some(location=> location.toLowerCase().includes(query))|| animal.threats.some(threat=> threat.toLowerCase().includes(query))): statusFiltered;}displayAnimals(filteredAnimals);}//==========MODAL SYSTEM==========function initModalFunctionality(){const modal=document.getElementById('animalModal');if(!modal)return;// Close modal when clicking outside modal.addEventListener('click',function(e){if(e.target===this){closeModal();}});// Close modal on escape key document.addEventListener('keydown',function(e){if(e.key==='Escape' && modal.style.display==='block'){closeModal();}});}function openModal(animalId){const animal=animalsData.find(a=> a.id===animalId);if(!animal){console.error('Animal not found with ID:',animalId);return;}const modalContent=document.getElementById('modalContent');if(!modalContent)return;modalContent.innerHTML=` <img src="${animal.image}" alt="${animal.name}" class="modal-image" onerror="this.src='https://via.placeholder.com/800x300?text=No+Image'"> <h2>${animal.name}</h2> <p class="scientific-name" style="font-size: 1.1rem;margin-bottom: 1rem;"> ${animal.scientificName}</p> <div style="display: grid;grid-template-columns: repeat(auto-fit,minmax(250px,1fr));gap: 2rem;margin-bottom: 2rem;"> <div> <h3>Basic Information</h3> <p><strong>Conservation Status:</strong> <span class="status-badge status-${animal.status}">${animal.statusText}</span> </p> <p><strong>Habitat:</strong> ${animal.habitat}</p> <p><strong>Population:</strong> ${animal.population}</p> </div> <div> <h3>Geographic Locations</h3> <ul class="locations-list"> ${animal.locations.map(location=> `<li>${location}</li>`).join('')}</ul> </div> </div> <div style="margin-bottom: 2rem;"> <h3>Description</h3> <p style="line-height: 1.6;">${animal.description}</p> </div> <div style="margin-bottom: 2rem;"> <h3>Main Threats</h3> <ul class="threats-list"> ${animal.threats.map(threat=> `<li>${threat}</li>`).join('')}</ul> </div> <div style="margin-bottom: 2rem;"> <h3>Fun Facts</h3> <ul class="facts-list"> ${animal.facts.map(fact=> `<li>${fact}</li>`).join('')}</ul> </div> <div> <h3>How You Can Help</h3> <div class="organizations"> ${animal.organizations.map(org=> ` <div class="org-card"> <h4>${org.name}</h4> <div class="org-links"> <a href="${org.website}" target="_blank" class="org-link visit-link"> Visit Website </a> <a href="${org.donate}" target="_blank" class="org-link donate-link"> Donate Now </a> </div> </div> `).join('')}</div> </div> `;const modal=document.getElementById('animalModal');modal.style.display='block';document.body.style.overflow='hidden';}function closeModal(){const modal=document.getElementById('animalModal');if(modal){modal.style.display='none';document.body.style.overflow='auto';}}//==========UTILITY FUNCTIONS==========function truncateText(text,maxLength){if(!text || typeof text !=='string')return '';return text.length <=maxLength ? text : text.substr(0,maxLength)+'...';}function showNoResultsMessage(message){const noResults=document.getElementById('noResults');if(noResults){noResults.innerHTML=` <div class="no-results-content"> <i class="fa-solid fa-search" style="font-size: 3rem;color: #ccc;margin-bottom: 1rem;"></i> <h3>No Results Found</h3> <p>${message}</p> </div> `;noResults.style.display='block';}}function debounce(func,wait){let timeout;return function executedFunction(...args){const later=()=>{clearTimeout(timeout);func(...args);};clearTimeout(timeout);timeout=setTimeout(later,wait);};}//==========CONSOLE LOGGING==========console.log("%c🦁 Endangered Animals Explorer","font-size: 18px;font-weight: bold;color: #ff6b35;");console.log("%cProtecting wildlife through awareness and action! 🌍","font-size: 14px;color: #666;");