//==========DATA==========const extinctData=[{name: "Dodo",time: "1681",img: "../../assets/images/extinct/dodo.jpg",desc: "Flightless bird native to Mauritius.",habitat: "Mauritius",diet: "Fruits & Seeds",cause: "Overhunting & invasive species"},{name: "Tasmanian Tiger(Thylacine)",time: "1936",img: "../../assets/images/extinct/Tasmanian Tiger.jpg",desc: "Carnivorous marsupial with stripes.",habitat: "Tasmania,Australia",diet: "Meat",cause: "Hunting & habitat loss"},{name: "Woolly Mammoth",time: "~2000 BCE",img: "../../assets/images/extinct/Woolly Mammoth.webp",desc: "Massive Ice Age mammal with long tusks.",habitat: "Northern Hemisphere",diet: "Grasses & herbs",cause: "Climate change & human hunting"},{name: "Passenger Pigeon",time: "1914",img: "../../assets/images/extinct/Passenger Pigeon.webp",desc: "Once the most abundant bird in North America.",habitat: "North America",diet: "Seeds",cause: "Overhunting & deforestation"},{name: "Great Auk",time: "1844",img: "../../assets/images/extinct/Great Auk.jpg",desc: "Large flightless seabird.",habitat: "North Atlantic",diet: "Fish",cause: "Overhunting"},{name: "Sabre-Toothed Cat(Smilodon)",time: "10,000 BCE",img: "../../assets/images/extinct/Sabre-Toothed Cat.jpg",desc: "Prehistoric predator with long canines.",habitat: "Americas",diet: "Large mammals",cause: "Climate change & prey loss"},{name: "Steller's Sea Cow",time: "1768",img: "../../assets/images/extinct/Steller.jpg",desc: "Huge marine herbivore.",habitat: "North Pacific",diet: "Kelp & seaweed",cause: "Intensive hunting"},{name: "Aurochs",time: "1627",img: "../../assets/images/extinct/Aurochs.webp",desc: "Wild ancestor of modern cattle.",habitat: "Eurasia",diet: "Grasses",cause: "Habitat loss & hunting"},{name: "Irish Elk",time: "7,700 BCE",img: "../../assets/images/extinct/Irish Elk.jpg",desc: "Giant deer with massive antlers.",habitat: "Europe & Asia",diet: "Vegetation",cause: "Climate change & food scarcity"}];//==========GLOBAL STATE==========let eIndex=0;//==========INITIALIZATION==========document.addEventListener("DOMContentLoaded",function(){// Initialize all components initLoadMoreFunctionality();initModalFunctionality();// Initial render render("extinctGrid",extinctData,6);});//==========RENDERING FUNCTIONS==========function render(gridId,data,count){const grid=document.getElementById(gridId);if(!grid){console.error('Grid container not found:',gridId);return;}// Get the slice of data to render const itemsToRender=data.slice(eIndex,eIndex+count);itemsToRender.forEach(animal=>{const card=document.createElement("div");card.className="animal-card";card.setAttribute("data-aos","fade-up");card.innerHTML=` ${animal.time ? `<span class="time-badge">${animal.time}</span>` : ""}<img src="${animal.img}" alt="${animal.name}" onerror="this.src='https://via.placeholder.com/300x200?text=No+Image'"> <div class="card-content"> <h4>${animal.name}</h4> <p>${animal.desc}</p> </div> `;// Add click handler for modal card.onclick=()=> openModal(animal);grid.appendChild(card);});// Update global index eIndex+=count;// Hide Load More button if all data is loaded const loadMoreBtn=document.getElementById("loadMoreBtn");if(loadMoreBtn && eIndex >=data.length){loadMoreBtn.style.display="none";}}//==========LOAD MORE FUNCTIONALITY==========function initLoadMoreFunctionality(){const loadBtn=document.getElementById("loadMoreBtn");if(!loadBtn)return;loadBtn.addEventListener("click",function(){// Load next batch of 6 items render("extinctGrid",extinctData,6);// Add loading animation feedback this.style.transform="scale(0.95)";setTimeout(()=>{this.style.transform="scale(1)";},150);});}//==========MODAL SYSTEM==========function initModalFunctionality(){const modal=document.getElementById("animalModal");if(!modal)return;// Close modal when clicking outside modal.addEventListener("click",function(e){if(e.target===this){closeModal();}});// Close modal on escape key document.addEventListener("keydown",function(e){if(e.key==="Escape" && modal.classList.contains("active")){closeModal();}});}function openModal(animal){const modal=document.getElementById("animalModal");if(!modal)return;// Update modal content const modalImage=document.getElementById("mImg");const modalName=document.getElementById("mName");const modalDesc=document.getElementById("mDesc");const modalHabitat=document.getElementById("mHabitat");const modalDiet=document.getElementById("mDiet");const modalCause=document.getElementById("mCause");const modalTime=document.getElementById("mTime");if(modalImage)modalImage.src=animal.img;if(modalName)modalName.textContent=animal.name;if(modalDesc)modalDesc.textContent=animal.desc;if(modalHabitat)modalHabitat.textContent=animal.habitat;if(modalDiet)modalDiet.textContent=animal.diet;if(modalCause)modalCause.textContent=animal.cause;if(modalTime)modalTime.textContent=animal.time ?? "—";// Handle image loading errors if(modalImage){modalImage.onerror=function(){this.src='https://via.placeholder.com/500x300?text=No+Image';};}// Show modal modal.classList.add("active");document.body.style.overflow="hidden";}function closeModal(){const modal=document.getElementById("animalModal");if(!modal)return;modal.classList.remove("active");document.body.style.overflow="auto";}//==========UTILITY FUNCTIONS==========function getExtinctionStats(){const causes=extinctData.reduce((acc,animal)=>{const primaryCause=animal.cause.split(' & ')[0];// Get primary cause acc[primaryCause]=(acc[primaryCause] || 0)+1;return acc;},{});return{total: extinctData.length,causes: causes,recent: extinctData.filter(animal=>{const year=parseInt(animal.time);return !isNaN(year)&& year > 1800;}).length};}//==========CONSOLE LOGGING==========console.log("%c🦕 Extinct Animals Memorial","font-size: 18px;font-weight: bold;color: #8b4513;");console.log("%cRemembering species lost to extinction-lessons for the future! 🌱","font-size: 14px;color: #666;");// Export functions for potential use in other modules if(typeof module !=='undefined' && module.exports){module.exports={extinctData,getExtinctionStats,render,openModal,closeModal};}