//==========INITIALIZATION==========document.addEventListener('DOMContentLoaded',function(){// Initialize all components initSmoothScrolling();initPreventionCards();initHeroBackground();});function initSmoothScrolling(){const links=document.querySelectorAll('a[href^="#"]');links.forEach(link=>{link.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href').substring(1);const targetElement=document.getElementById(targetId);if(targetElement){targetElement.scrollIntoView({behavior: 'smooth',block: 'start'});}});});}function initPreventionCards(){const cards=document.querySelectorAll('.prevention-card,.practice-card');cards.forEach(card=>{card.addEventListener('mouseenter',function(){this.style.transform='translateY(-10px)scale(1.02)';});card.addEventListener('mouseleave',function(){this.style.transform='translateY(0)scale(1)';});});}function initHeroBackground(){const heroBg=document.getElementById('heroBg');let lastScrollY=window.scrollY;window.addEventListener('scroll',()=>{const currentScrollY=window.scrollY;const deltaY=currentScrollY-lastScrollY;// Subtle parallax effect const translateY=currentScrollY * 0.5;heroBg.style.transform=`translateY(${translateY}px)scale(${1+currentScrollY * 0.0002})`;lastScrollY=currentScrollY;});}function isInViewport(element){const rect=element.getBoundingClientRect();return(rect.top >=0 && rect.left >=0 && rect.bottom <=(window.innerHeight || document.documentElement.clientHeight)&& rect.right <=(window.innerWidth || document.documentElement.clientWidth));}window.addEventListener('scroll',function(){const sections=document.querySelectorAll('.prevention-section,.behavior-section,.response-section,.habitat-section,.practices-section');sections.forEach(section=>{if(isInViewport(section)){section.classList.add('animate-in');}});});//==========RESPONSIVE BEHAVIOR==========window.addEventListener('resize',function(){// Reinitialize components if needed for responsive design initPreventionCards();});//==========ACCESSIBILITY==========document.addEventListener('keydown',function(e){// Add keyboard navigation for cards if needed if(e.key==='Enter' || e.key===' '){const focusedElement=document.activeElement;if(focusedElement.classList.contains('prevention-card')|| focusedElement.classList.contains('practice-card')){focusedElement.click();}}});