//===========================================// WILDLIFE PHOTOGRAPHY ETHICS-JAVASCRIPT //===========================================document.addEventListener('DOMContentLoaded',function(){//===========================================// ACCORDION FUNCTIONALITY //===========================================const accordionHeaders=document.querySelectorAll('.accordion-header');accordionHeaders.forEach(header=>{header.addEventListener('click',function(){const accordionItem=this.parentElement;const isActive=accordionItem.classList.contains('active');// Close all accordion items document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.remove('active');});// Open clicked item if it wasn't active if(!isActive){accordionItem.classList.add('active');}});// Keyboard accessibility header.setAttribute('tabindex','0');header.setAttribute('role','button');header.setAttribute('aria-expanded','false');header.addEventListener('keydown',function(e){if(e.key==='Enter' || e.key===' '){e.preventDefault();this.click();const isExpanded=this.parentElement.classList.contains('active');this.setAttribute('aria-expanded',isExpanded);}});});//===========================================// PLEDGE MODAL FUNCTIONALITY //===========================================const pledgeModal=document.getElementById('pledgeModal');const pledgeBtn=document.getElementById('takePledgeBtn');const pledgeClose=document.querySelector('.pledge-close');const pledgeForm=document.getElementById('pledgeForm');// Open modal if(pledgeBtn){pledgeBtn.addEventListener('click',function(){pledgeModal.style.display='block';document.body.style.overflow='hidden';});}// Close modal if(pledgeClose){pledgeClose.addEventListener('click',function(){pledgeModal.style.display='none';document.body.style.overflow='auto';});}// Close on outside click window.addEventListener('click',function(e){if(e.target===pledgeModal){pledgeModal.style.display='none';document.body.style.overflow='auto';}});// Close on Escape key document.addEventListener('keydown',function(e){if(e.key==='Escape' && pledgeModal.style.display==='block'){pledgeModal.style.display='none';document.body.style.overflow='auto';}});// Handle form submission if(pledgeForm){pledgeForm.addEventListener('submit',function(e){e.preventDefault();const name=document.getElementById('pledgeName').value;const email=document.getElementById('pledgeEmail').value;const agree=document.getElementById('pledgeAgree').checked;if(name && agree){// Store pledge in localStorage const pledgeData={name: name,email: email || 'Not provided',date: new Date().toISOString(),timestamp: Date.now()};localStorage.setItem('wildlifePhotographyPledge',JSON.stringify(pledgeData));// Show success message const modalContent=document.querySelector('.pledge-modal-content');modalContent.innerHTML=` <span class="pledge-close">&times;</span> <div style="text-align: center;padding: 40px 20px;"> <i class="fas fa-check-circle" style="font-size: 5rem;color: #10b981;margin-bottom: 20px;"></i> <h3 style="color: #10b981;margin-bottom: 15px;">Pledge Submitted Successfully!</h3> <p style="font-size: 1.1rem;color: #666;margin-bottom: 20px;"> Thank you,<strong>${name}</strong>,for committing to ethical wildlife photography. </p> <p style="color: #888;font-size: 0.95rem;"> Your pledge has been recorded. Together,we can protect wildlife while capturing its beauty. </p> <button onclick="location.reload()" style="margin-top: 30px;padding: 12px 30px;background: linear-gradient(135deg,#10b981,#059669);color: white;border: none;border-radius: 25px;font-size: 1rem;font-weight: 600;cursor: pointer;"> Continue Learning </button> </div> `;// Re-attach close button functionality const newCloseBtn=document.querySelector('.pledge-close');newCloseBtn.addEventListener('click',function(){pledgeModal.style.display='none';document.body.style.overflow='auto';location.reload();});// Log pledge(in production,this would send to a server)console.log('Wildlife Photography Pledge:',pledgeData);}});}// Check if user has already taken the pledge const existingPledge=localStorage.getItem('wildlifePhotographyPledge');if(existingPledge && pledgeBtn){const pledgeData=JSON.parse(existingPledge);const pledgeDate=new Date(pledgeData.date).toLocaleDateString();pledgeBtn.innerHTML=` <i class="fas fa-certificate"></i> Pledge Taken(${pledgeDate})`;pledgeBtn.style.background='#10b981';pledgeBtn.title=`You took the pledge on ${pledgeDate}`;}//===========================================// SMOOTH SCROLLING //===========================================document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){const href=this.getAttribute('href');if(href==='#' || !href){e.preventDefault();return;}const target=document.querySelector(href);if(target){e.preventDefault();const headerOffset=80;const elementPosition=target.getBoundingClientRect().top;const offsetPosition=elementPosition+window.pageYOffset-headerOffset;window.scrollTo({top: offsetPosition,behavior: 'smooth'});}});});//===========================================// CARD HOVER EFFECTS //===========================================const principleCards=document.querySelectorAll('.principle-card');principleCards.forEach(card=>{card.addEventListener('mouseenter',function(){const icon=this.querySelector('.principle-icon');if(icon){icon.style.transform='scale(1.1)rotate(5deg)';icon.style.transition='all 0.3s ease';}});card.addEventListener('mouseleave',function(){const icon=this.querySelector('.principle-icon');if(icon){icon.style.transform='scale(1)rotate(0deg)';}});});//===========================================// SIGNAL CARDS ANIMATION //===========================================const signalCards=document.querySelectorAll('.signal-card');signalCards.forEach(card=>{card.addEventListener('mouseenter',function(){const icon=this.querySelector('.signal-icon');if(icon){icon.style.transform='scale(1.15)rotate(-5deg)';icon.style.transition='all 0.3s ease';}});card.addEventListener('mouseleave',function(){const icon=this.querySelector('.signal-icon');if(icon){icon.style.transform='scale(1)rotate(0deg)';}});});//===========================================// CASE STUDY CARDS INTERACTION //===========================================const caseCards=document.querySelectorAll('.case-card');caseCards.forEach(card=>{card.addEventListener('mouseenter',function(){const badge=this.querySelector('.case-badge');if(badge){badge.style.transform='scale(1.05)';badge.style.transition='all 0.3s ease';}});card.addEventListener('mouseleave',function(){const badge=this.querySelector('.case-badge');if(badge){badge.style.transform='scale(1)';}});});//===========================================// EQUIPMENT CARDS INTERACTION //===========================================const equipmentCards=document.querySelectorAll('.equipment-card');equipmentCards.forEach(card=>{card.addEventListener('mouseenter',function(){const icon=this.querySelector('.equipment-icon');if(icon){icon.style.transform='rotateY(180deg)';icon.style.transition='all 0.6s ease';}});card.addEventListener('mouseleave',function(){const icon=this.querySelector('.equipment-icon');if(icon){icon.style.transform='rotateY(0deg)';}});});//===========================================// RESOURCE CARDS HOVER //===========================================const resourceCards=document.querySelectorAll('.resource-card');resourceCards.forEach(card=>{card.addEventListener('mouseenter',function(){const link=this.querySelector('.resource-link');if(link){link.style.textDecoration='underline';}});card.addEventListener('mouseleave',function(){const link=this.querySelector('.resource-link');if(link){link.style.textDecoration='none';}});});//===========================================// SCROLL-TRIGGERED ANIMATIONS //===========================================const observerOptions={threshold: 0.1,rootMargin: '0px 0px-50px 0px'};const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('aos-animate');}});},observerOptions);document.querySelectorAll('[data-aos]').forEach(element=>{observer.observe(element);});//===========================================// PRACTICES LIST ANIMATION //===========================================const practiceItems=document.querySelectorAll('.practices-list li');const practiceObserver=new IntersectionObserver((entries)=>{entries.forEach((entry,index)=>{if(entry.isIntersecting){setTimeout(()=>{entry.target.style.opacity='1';entry.target.style.transform='translateX(0)';},index * 50);}});},{threshold: 0.2});practiceItems.forEach(item=>{item.style.opacity='0';item.style.transform='translateX(-20px)';item.style.transition='all 0.5s ease';practiceObserver.observe(item);});//===========================================// PARALLAX EFFECT FOR HERO ICON //===========================================const heroIcon=document.querySelector('.hero-camera-icon');if(heroIcon){window.addEventListener('scroll',()=>{const scrolled=window.pageYOffset;const rate=scrolled * 0.4;heroIcon.style.transform=`translateY(calc(-50%+${rate}px))rotate(${scrolled * 0.05}deg)`;});}//===========================================// SCROLL TO TOP BUTTON //===========================================const scrollToTopBtn=document.createElement('button');scrollToTopBtn.innerHTML='<i class="fas fa-arrow-up"></i>';scrollToTopBtn.className='scroll-to-top';scrollToTopBtn.setAttribute('aria-label','Scroll to top');scrollToTopBtn.style.cssText=` position: fixed;bottom: 30px;right: 30px;width: 50px;height: 50px;border-radius: 50%;background: linear-gradient(135deg,#3b82f6,#1e3a8a);color: white;border: none;font-size: 1.2rem;cursor: pointer;opacity: 0;visibility: hidden;transition: all 0.3s ease;z-index: 1000;box-shadow: 0 5px 20px rgba(59,130,246,0.3);`;document.body.appendChild(scrollToTopBtn);window.addEventListener('scroll',()=>{if(window.pageYOffset > 300){scrollToTopBtn.style.opacity='1';scrollToTopBtn.style.visibility='visible';}else{scrollToTopBtn.style.opacity='0';scrollToTopBtn.style.visibility='hidden';}});scrollToTopBtn.addEventListener('click',()=>{window.scrollTo({top: 0,behavior: 'smooth'});});scrollToTopBtn.addEventListener('mouseenter',function(){this.style.transform='translateY(-5px)scale(1.1)';});scrollToTopBtn.addEventListener('mouseleave',function(){this.style.transform='translateY(0)scale(1)';});//===========================================// HIGHLIGHT CURRENT SECTION IN VIEW //===========================================const sections=document.querySelectorAll('section[class*="-section"]');const sectionObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){// Could be used to update navigation or add visual feedback console.log('Viewing section:',entry.target.className);}});},{threshold: 0.3});sections.forEach(section=>{sectionObserver.observe(section);});//===========================================// COPY LINK TO CLIPBOARD(for sharing)//===========================================function createShareButton(){const shareBtn=document.createElement('button');shareBtn.innerHTML='<i class="fas fa-share-alt"></i>';shareBtn.className='share-button';shareBtn.setAttribute('aria-label','Share this page');shareBtn.style.cssText=` position: fixed;bottom: 90px;right: 30px;width: 50px;height: 50px;border-radius: 50%;background: linear-gradient(135deg,#10b981,#059669);color: white;border: none;font-size: 1.2rem;cursor: pointer;transition: all 0.3s ease;z-index: 1000;box-shadow: 0 5px 20px rgba(16,185,129,0.3);`;document.body.appendChild(shareBtn);shareBtn.addEventListener('click',function(){const url=window.location.href;if(navigator.share){navigator.share({title: 'Wildlife Photography Ethics',text: 'Learn responsible wildlife photography practices that respect animals and habitats.',url: url}).catch(err=> console.log('Error sharing:',err));}else{// Fallback: copy to clipboard navigator.clipboard.writeText(url).then(()=>{const originalHTML=this.innerHTML;this.innerHTML='<i class="fas fa-check"></i>';this.style.background='linear-gradient(135deg,#10b981,#059669)';setTimeout(()=>{this.innerHTML=originalHTML;},2000);});}});shareBtn.addEventListener('mouseenter',function(){this.style.transform='translateY(-5px)scale(1.1)';});shareBtn.addEventListener('mouseleave',function(){this.style.transform='translateY(0)scale(1)';});}createShareButton();//===========================================// PRINT FUNCTIONALITY //===========================================window.addEventListener('beforeprint',()=>{// Expand all accordions for printing document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.add('active');});});window.addEventListener('afterprint',()=>{// Collapse all accordions after printing document.querySelectorAll('.accordion-item').forEach(item=>{item.classList.remove('active');});});//===========================================// READING PROGRESS BAR //===========================================const progressBar=document.createElement('div');progressBar.style.cssText=` position: fixed;top: 0;left: 0;width: 0%;height: 4px;background: linear-gradient(90deg,#3b82f6,#10b981);z-index: 9999;transition: width 0.1s ease;`;document.body.appendChild(progressBar);window.addEventListener('scroll',()=>{const winScroll=document.body.scrollTop || document.documentElement.scrollTop;const height=document.documentElement.scrollHeight-document.documentElement.clientHeight;const scrolled=(winScroll / height)* 100;progressBar.style.width=scrolled+'%';});//===========================================// LOG PAGE VIEW //===========================================console.log('Wildlife Photography Ethics page loaded successfully');// Track time spent on page let pageLoadTime=new Date().getTime();window.addEventListener('beforeunload',()=>{let timeSpent=(new Date().getTime()-pageLoadTime)/ 1000;console.log(`User spent ${Math.round(timeSpent)}seconds learning about wildlife photography ethics`);});//===========================================// KEYBOARD SHORTCUTS //===========================================document.addEventListener('keydown',function(e){// P key to take pledge if(e.key==='p' || e.key==='P'){if(pledgeModal && pledgeModal.style.display !=='block'){pledgeBtn.click();}}});// Show keyboard shortcuts hint console.log('%cKeyboard Shortcuts:','font-weight: bold;font-size: 14px;color: #3b82f6;');console.log('Press "P" to take the Wildlife Photography Ethics Pledge');});