//====================================// Sample Events Data //====================================const sampleEvents=[{id: 1,title: "Marina Beach Cleanup Drive",category: "cleanup",date: "2026-01-25",time: "07:00",location: "Marina Beach,Chennai",coordinates: [13.0500,80.2824],description: "Join us for a morning beach cleanup! We'll provide gloves,bags,and refreshments. Together,let's make Marina Beach cleaner and safer for marine life.",organizer: "EcoLife Chennai",maxParticipants: 50,attendees: 32,image: "https://images.unsplash.com/photo-1618477461853-cf6ed80faba5?w=600"},{id: 2,title: "Urban Forest Plantation",category: "plantation",date: "2026-01-28",time: "08:00",location: "Guindy National Park,Chennai",coordinates: [13.0067,80.2206],description: "Help us plant 500 native trees to create an urban forest. All saplings and tools will be provided. Wear comfortable clothes and bring your enthusiasm!",organizer: "Green Earth Foundation",maxParticipants: 100,attendees: 67,image: "https://images.unsplash.com/photo-1542601906990-b4d3fb778b09?w=600"},{id: 3,title: "Wildlife Photography Workshop",category: "workshop",date: "2026-02-01",time: "09:00",location: "Theosophical Society,Adyar",coordinates: [13.0205,80.2579],description: "Learn wildlife photography from professionals. Understand animal behavior,camera settings,and ethical photography practices. Cameras provided for beginners.",organizer: "Nature Photographers Club",maxParticipants: 25,attendees: 18,image: "https://images.unsplash.com/photo-1549366021-9f761d450615?w=600"},{id: 4,title: "Stray Animal Feeding Program",category: "animal-care",date: "2026-01-26",time: "06:30",location: "Anna Nagar,Chennai",coordinates: [13.0850,80.2101],description: "Daily feeding program for stray dogs and cats. We need volunteers to help distribute food and water. Training provided for first-time volunteers.",organizer: "Paws & Care NGO",maxParticipants: 20,attendees: 12,image: "https://images.unsplash.com/photo-1601758228041-f3b2795255f1?w=600"},{id: 5,title: "Lake Restoration Project",category: "cleanup",date: "2026-02-05",time: "07:30",location: "Perungudi Lake,Chennai",coordinates: [12.9539,80.2371],description: "Help restore the Perungudi Lake by removing invasive plants,picking up trash,and planting native aquatic species. Boats and equipment provided.",organizer: "Lake Conservation Society",maxParticipants: 40,attendees: 28,image: "https://images.unsplash.com/photo-1519451241324-20b4ea2c4220?w=600"},{id: 6,title: "Composting Workshop",category: "workshop",date: "2026-02-08",time: "10:00",location: "Community Center,T. Nagar",coordinates: [13.0418,80.2341],description: "Learn how to turn kitchen waste into nutrient-rich compost. Hands-on session with take-home starter kit included. Perfect for beginners!",organizer: "Zero Waste Chennai",maxParticipants: 30,attendees: 24,image: "https://images.unsplash.com/photo-1416879595882-3373a0480b5b?w=600"}];//====================================// Global State //====================================let events=[];let userRSVPs=new Set();let map=null;let markers=[];//====================================// Initialize //====================================document.addEventListener('DOMContentLoaded',()=>{loadEvents();initializeMap();renderEvents();setupEventListeners();loadUserRSVPs();updateStats();});//====================================// Events Data Management //====================================function loadEvents(){const storedEvents=localStorage.getItem('communityEvents');if(storedEvents){events=JSON.parse(storedEvents);// Merge with sample events if needed const sampleIds=sampleEvents.map(e=> e.id);events=events.filter(e=> !sampleIds.includes(e.id));events=[...sampleEvents,...events];}else{events=[...sampleEvents];}saveEvents();}function saveEvents(){localStorage.setItem('communityEvents',JSON.stringify(events));}function loadUserRSVPs(){const storedRSVPs=localStorage.getItem('userRSVPs');if(storedRSVPs){userRSVPs=new Set(JSON.parse(storedRSVPs));}}function saveUserRSVPs(){localStorage.setItem('userRSVPs',JSON.stringify([...userRSVPs]));}//====================================// Interactive Map(Leaflet.js)//====================================function initializeMap(){const mapElement=document.getElementById('events-map');if(!mapElement)return;// Initialize map centered on Chennai map=L.map('events-map').setView([13.0827,80.2707],12);// Add OpenStreetMap tiles L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',{attribution: '&copy;<a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'}).addTo(map);// Add event markers addEventMarkers();}function addEventMarkers(){if(!map)return;// Clear existing markers markers.forEach(marker=> map.removeLayer(marker));markers=[];// Category colors const categoryColors={'cleanup': '#3b82f6','plantation': '#10b981','workshop': '#8b5cf6','animal-care': '#f59e0b'};const categoryIcons={'cleanup': 'ðŸ§¹','plantation': 'ðŸŒ±','workshop': 'ðŸ“š','animal-care': 'ðŸ¾'};events.forEach(event=>{if(event.coordinates){const icon=L.divIcon({className: 'custom-marker',html: `<div style=" background: ${categoryColors[event.category]};width: 40px;height: 40px;border-radius: 50%;display: flex;align-items: center;justify-content: center;font-size: 18px;box-shadow: 0 4px 15px ${categoryColors[event.category]}80;border: 3px solid white;">${categoryIcons[event.category]}</div>`,iconSize: [40,40],iconAnchor: [20,40]});const marker=L.marker(event.coordinates,{icon}).addTo(map);const popupContent=` <div class="map-popup"> <h4>${event.title}</h4> <p><i class="fa-solid fa-calendar"></i> ${formatDate(event.date)}at ${formatTime(event.time)}</p> <p><i class="fa-solid fa-location-dot"></i> ${event.location}</p> <button class="popup-btn" onclick="showEventDetails(${event.id})">View Details</button> </div> `;marker.bindPopup(popupContent);markers.push(marker);}});}//====================================// Render Events //====================================function renderEvents(filteredEvents=null){const eventsGrid=document.getElementById('eventsGrid');if(!eventsGrid)return;const eventsToRender=filteredEvents || events;if(eventsToRender.length===0){eventsGrid.innerHTML=` <div class="no-events" style="grid-column: 1/-1;text-align: center;padding: 60px 20px;"> <i class="fa-solid fa-calendar-xmark" style="font-size: 4rem;color: #9ca3af;margin-bottom: 20px;"></i> <h3 style="color: var(--text-primary,#1f2937);margin-bottom: 10px;">No Events Found</h3> <p style="color: var(--text-secondary,#6b7280);">Try adjusting your filters or create a new event!</p> </div> `;return;}eventsGrid.innerHTML=eventsToRender.map(event=> createEventCard(event)).join('');// Add click handlers to RSVP buttons document.querySelectorAll('.rsvp-btn').forEach(btn=>{btn.addEventListener('click',(e)=>{e.stopPropagation();const eventId=parseInt(btn.dataset.eventId);toggleRSVP(eventId,btn);});});// Add click handlers to event cards document.querySelectorAll('.event-card').forEach(card=>{card.addEventListener('click',()=>{const eventId=parseInt(card.dataset.eventId);showEventDetails(eventId);});});}function createEventCard(event){const isRegistered=userRSVPs.has(event.id);const dateObj=new Date(event.date);const day=dateObj.getDate();const month=dateObj.toLocaleString('default',{month: 'short'});const categoryLabels={'cleanup': 'ðŸ§¹ Cleanup','plantation': 'ðŸŒ± Plantation','workshop': 'ðŸ“š Workshop','animal-care': 'ðŸ¾ Animal Care'};const spotsLeft=event.maxParticipants ? event.maxParticipants-event.attendees : null;return ` <article class="event-card" data-event-id="${event.id}" data-aos="fade-up"> <div class="event-card-image"> <img src="${event.image || 'https://images.unsplash.com/photo-1558008258-3256797b43f3?w=600'}" alt="${event.title}" loading="lazy"> <span class="event-category-badge ${event.category}">${categoryLabels[event.category]}</span> <div class="event-date-badge"> <span class="day">${day}</span> <span class="month">${month}</span> </div> </div> <div class="event-card-content"> <h3 class="event-title">${event.title}</h3> <div class="event-meta"> <div class="event-meta-item"> <i class="fa-regular fa-clock"></i> <span>${formatTime(event.time)}</span> </div> <div class="event-meta-item"> <i class="fa-solid fa-location-dot"></i> <span>${event.location}</span> </div> </div> <p class="event-description">${event.description}</p> <div class="event-footer"> <div class="event-attendees"> <div class="attendee-avatars"> ${generateAvatars(event.attendees)}</div> <span class="attendee-count"> ${event.attendees}joined${spotsLeft !==null ? ` â€¢ ${spotsLeft}spots left` : ''}</span> </div> <button class="rsvp-btn ${isRegistered ? 'registered' : 'available'}" data-event-id="${event.id}"> ${isRegistered ? '<i class="fa-solid fa-check"></i> Registered' : 'RSVP'}</button> </div> </div> </article> `;}function generateAvatars(count){const maxAvatars=3;const displayCount=Math.min(count,maxAvatars);let avatars='';const initials=['A','B','C','D','E','F','G','H'];for(let i=0;i < displayCount;i++){avatars+=`<div class="attendee-avatar">${initials[i]}</div>`;}if(count > maxAvatars){avatars+=`<div class="attendee-avatar">+${count-maxAvatars}</div>`;}return avatars;}//====================================// Event Details Modal //====================================function showEventDetails(eventId){const event=events.find(e=> e.id===eventId);if(!event)return;const modal=document.getElementById('eventDetailsModal');const content=document.getElementById('eventDetailsContent');const isRegistered=userRSVPs.has(eventId);const categoryLabels={'cleanup': 'ðŸ§¹ Cleanup','plantation': 'ðŸŒ± Plantation','workshop': 'ðŸ“š Workshop','animal-care': 'ðŸ¾ Animal Care'};const categoryColors={'cleanup': '#3b82f6','plantation': '#10b981','workshop': '#8b5cf6','animal-care': '#f59e0b'};content.innerHTML=` <div class="event-details-image"> <img src="${event.image || 'https://images.unsplash.com/photo-1558008258-3256797b43f3?w=600'}" alt="${event.title}"> </div> <div class="event-details-body"> <div class="event-details-header"> <span class="event-details-category" style="background: ${categoryColors[event.category]}">${categoryLabels[event.category]}</span> <h2 class="event-details-title">${event.title}</h2> </div> <div class="event-details-meta"> <div class="detail-item"> <i class="fa-regular fa-calendar"></i> <span>${formatDate(event.date)}</span> </div> <div class="detail-item"> <i class="fa-regular fa-clock"></i> <span>${formatTime(event.time)}</span> </div> <div class="detail-item"> <i class="fa-solid fa-location-dot"></i> <span>${event.location}</span> </div> <div class="detail-item"> <i class="fa-solid fa-users"></i> <span>${event.attendees}${event.maxParticipants ? ` / ${event.maxParticipants}` : ''}Attendees</span> </div> </div> <p class="event-details-description">${event.description}</p> <div class="event-details-organizer"> <div class="organizer-avatar">${event.organizer.charAt(0)}</div> <div class="organizer-info"> <span class="organizer-label">Organized by</span> <span class="organizer-name">${event.organizer}</span> </div> </div> <div class="event-details-action"> <button class="btn-primary" id="detailsRsvpBtn" data-event-id="${eventId}"> ${isRegistered ? '<i class="fa-solid fa-check"></i> You\'re Registered!' : '<i class="fa-solid fa-calendar-plus"></i> RSVP Now'}</button> </div> </div> `;modal.classList.add('active');// Add RSVP handler document.getElementById('detailsRsvpBtn').addEventListener('click',(e)=>{const eventId=parseInt(e.target.dataset.eventId);toggleRSVP(eventId,e.target);// Update button text const isNowRegistered=userRSVPs.has(eventId);e.target.innerHTML=isNowRegistered ? '<i class="fa-solid fa-check"></i> You\'re Registered!' : '<i class="fa-solid fa-calendar-plus"></i> RSVP Now';});}// Make function globally available for map popups window.showEventDetails=showEventDetails;//====================================// RSVP System //====================================function toggleRSVP(eventId,button){const event=events.find(e=> e.id===eventId);if(!event)return;if(userRSVPs.has(eventId)){// Cancel RSVP userRSVPs.delete(eventId);event.attendees=Math.max(0,event.attendees-1);button.classList.remove('registered');button.classList.add('available');button.innerHTML='RSVP';showNotification('RSVP cancelled successfully');}else{// Check if event is full if(event.maxParticipants && event.attendees >=event.maxParticipants){showNotification('Sorry,this event is full!','error');return;}// Add RSVP userRSVPs.add(eventId);event.attendees++;button.classList.remove('available');button.classList.add('registered');button.innerHTML='<i class="fa-solid fa-check"></i> Registered';showNotification('RSVP confirmed! See you there! ðŸŽ‰');}saveEvents();saveUserRSVPs();renderEvents(getFilteredEvents());updateStats();}//====================================// Create Event //====================================function setupEventListeners(){// Create Event Modal const createEventBtn=document.getElementById('createEventBtn');const createEventModal=document.getElementById('createEventModal');const closeModal=document.getElementById('closeModal');const cancelEventBtn=document.getElementById('cancelEventBtn');const eventForm=document.getElementById('eventForm');if(createEventBtn){createEventBtn.addEventListener('click',()=>{createEventModal.classList.add('active');document.getElementById('eventDate').min=new Date().toISOString().split('T')[0];});}if(closeModal){closeModal.addEventListener('click',()=>{createEventModal.classList.remove('active');});}if(cancelEventBtn){cancelEventBtn.addEventListener('click',()=>{createEventModal.classList.remove('active');});}// Close modal on outside click if(createEventModal){createEventModal.addEventListener('click',(e)=>{if(e.target===createEventModal){createEventModal.classList.remove('active');}});}// Event Details Modal Close const closeDetailsModal=document.getElementById('closeDetailsModal');const eventDetailsModal=document.getElementById('eventDetailsModal');if(closeDetailsModal){closeDetailsModal.addEventListener('click',()=>{eventDetailsModal.classList.remove('active');});}if(eventDetailsModal){eventDetailsModal.addEventListener('click',(e)=>{if(e.target===eventDetailsModal){eventDetailsModal.classList.remove('active');}});}// Form Submission if(eventForm){eventForm.addEventListener('submit',handleEventSubmit);}// Filters const searchInput=document.getElementById('searchEvents');const categoryFilter=document.getElementById('categoryFilter');const dateFilter=document.getElementById('dateFilter');const locationFilter=document.getElementById('locationFilter');[searchInput,categoryFilter,dateFilter,locationFilter].forEach(filter=>{if(filter){filter.addEventListener('change',handleFiltersChange);filter.addEventListener('input',handleFiltersChange);}});// Load More Button const loadMoreBtn=document.getElementById('loadMoreBtn');if(loadMoreBtn){loadMoreBtn.addEventListener('click',()=>{showNotification('All events are already displayed');});}}function handleEventSubmit(e){e.preventDefault();const newEvent={id: Date.now(),// Unique ID title: document.getElementById('eventTitle').value,category: document.getElementById('eventCategory').value,date: document.getElementById('eventDate').value,time: document.getElementById('eventTime').value,location: document.getElementById('eventLocation').value,description: document.getElementById('eventDescription').value,maxParticipants: parseInt(document.getElementById('maxParticipants').value)|| null,image: document.getElementById('eventImage').value || null,organizer: 'You',attendees: 1,// Creator is first attendee coordinates: [13.0827+(Math.random()-0.5)* 0.1,80.2707+(Math.random()-0.5)* 0.1] // Random coordinates near Chennai};events.unshift(newEvent);userRSVPs.add(newEvent.id);saveEvents();saveUserRSVPs();renderEvents();addEventMarkers();updateStats();// Reset form and close modal e.target.reset();document.getElementById('createEventModal').classList.remove('active');showNotification('Event created successfully! ðŸŽ‰');}//====================================// Filtering //====================================function handleFiltersChange(){const filteredEvents=getFilteredEvents();renderEvents(filteredEvents);}function getFilteredEvents(){const searchTerm=document.getElementById('searchEvents')?.value.toLowerCase()|| '';const category=document.getElementById('categoryFilter')?.value || 'all';const dateRange=document.getElementById('dateFilter')?.value || 'all';return events.filter(event=>{// Search filter const matchesSearch=event.title.toLowerCase().includes(searchTerm)|| event.location.toLowerCase().includes(searchTerm)|| event.description.toLowerCase().includes(searchTerm);// Category filter const matchesCategory=category==='all' || event.category===category;// Date filter let matchesDate=true;const eventDate=new Date(event.date);const today=new Date();today.setHours(0,0,0,0);if(dateRange==='today'){matchesDate=eventDate.toDateString()===today.toDateString();}else if(dateRange==='week'){const weekFromNow=new Date(today);weekFromNow.setDate(weekFromNow.getDate()+7);matchesDate=eventDate >=today && eventDate <=weekFromNow;}else if(dateRange==='month'){const monthFromNow=new Date(today);monthFromNow.setMonth(monthFromNow.getMonth()+1);matchesDate=eventDate >=today && eventDate <=monthFromNow;}return matchesSearch && matchesCategory && matchesDate;});}//====================================// Notifications //====================================function showNotification(message,type='success'){const notification=document.getElementById('notification');const notificationText=document.getElementById('notificationText');if(notification && notificationText){notificationText.textContent=message;// Update icon based on type const icon=notification.querySelector('i');if(icon){icon.className=type==='success' ? 'fa-solid fa-check-circle' : 'fa-solid fa-exclamation-circle';}// Update colors for error if(type==='error'){notification.style.background='linear-gradient(135deg,#ef4444,#dc2626)';}else{notification.style.background='linear-gradient(135deg,#10b981,#059669)';}notification.classList.add('show');setTimeout(()=>{notification.classList.remove('show');},3000);}}//====================================// Update Stats //====================================function updateStats(){const totalEventsEl=document.getElementById('totalEvents');const totalVolunteersEl=document.getElementById('totalVolunteers');const treesPlantedEl=document.getElementById('treesPlanted');if(totalEventsEl){totalEventsEl.textContent=events.length;}if(totalVolunteersEl){const totalAttendees=events.reduce((sum,event)=> sum+event.attendees,0);totalVolunteersEl.textContent=totalAttendees.toLocaleString();}if(treesPlantedEl){// Calculate based on plantation events const plantationEvents=events.filter(e=> e.category==='plantation');const estimatedTrees=plantationEvents.reduce((sum,event)=> sum+(event.attendees * 10),0)|| 5600;treesPlantedEl.textContent=estimatedTrees.toLocaleString();}}//====================================// Utility Functions //====================================function formatDate(dateString){const options={weekday: 'long',year: 'numeric',month: 'long',day: 'numeric'};return new Date(dateString).toLocaleDateString('en-US',options);}function formatTime(timeString){const [hours,minutes]=timeString.split(':');const hour=parseInt(hours);const ampm=hour >=12 ? 'PM' : 'AM';const displayHour=hour % 12 || 12;return `${displayHour}:${minutes}${ampm}`;}//====================================// Scroll Progress //====================================window.addEventListener('scroll',()=>{const scrollProgress=document.getElementById('scrollProgress');if(scrollProgress){const scrollTop=window.scrollY;const docHeight=document.documentElement.scrollHeight-window.innerHeight;const scrollPercent=(scrollTop / docHeight)* 100;scrollProgress.style.width=scrollPercent+'%';}});