//==========WASTE DATA==========const wasteItems=[{name: "Banana Peel",type: "wet",icon: "fa-carrot"},// representing food{name: "Newspaper",type: "dry",icon: "fa-newspaper"},{name: "Battery",type: "hazard",icon: "fa-battery-full"},{name: "Plastic Bottle",type: "dry",icon: "fa-bottle-water"},{name: "Apple Core",type: "wet",icon: "fa-apple-whole"},{name: "Coke Can",type: "dry",icon: "fa-can-food"},// using can icon{name: "Old Medicine",type: "hazard",icon: "fa-pills"},{name: "Cardboard Box",type: "dry",icon: "fa-box-open"}];//==========GAME STATE==========let score=0;let currentItem=null;//==========DOM ELEMENTS==========const draggable=document.getElementById('draggable-item');const bins=document.querySelectorAll('.bin');const scoreDisplay=document.getElementById('score');const messageDisplay=document.getElementById('game-message');const restartBtn=document.getElementById('restart-btn');//==========GAME INITIALIZATION==========function startGame(){// Hide restart button and reset message restartBtn.style.display='none';messageDisplay.textContent="Drag the item to the correct bin!";messageDisplay.className="";// Select random waste item const randomIndex=Math.floor(Math.random()* wasteItems.length);currentItem=wasteItems[randomIndex];// Update draggable item UI draggable.querySelector('span').textContent=currentItem.name;draggable.querySelector('i').className=`fa-solid ${currentItem.icon}`;draggable.setAttribute('draggable','true');draggable.style.opacity="1";draggable.style.cursor="grab";}//==========DRAG AND DROP EVENTS==========// Draggable item events draggable.addEventListener('dragstart',(e)=>{// Store waste type in drag data e.dataTransfer.setData('text/plain',currentItem.type);// Temporarily hide element during drag setTimeout(()=> draggable.style.display="none",0);});draggable.addEventListener('dragend',()=>{// Show element again after drag ends draggable.style.display="flex";});// Bin events for each waste bin bins.forEach(bin=>{// Allow dropping on bin bin.addEventListener('dragover',(e)=>{e.preventDefault();bin.classList.add('drag-over');});// Remove hover effect when leaving bin bin.addEventListener('dragleave',()=>{bin.classList.remove('drag-over');});// Handle item drop on bin bin.addEventListener('drop',(e)=>{e.preventDefault();bin.classList.remove('drag-over');const selectedBinType=bin.getAttribute('data-type');checkAnswer(selectedBinType);});});//==========GAME LOGIC==========function checkAnswer(selectedBinType){if(selectedBinType===currentItem.type){// Correct answer score+=10;scoreDisplay.textContent=score;messageDisplay.textContent=`✅ Correct! ${currentItem.name}goes in ${selectedBinType}waste.`;messageDisplay.className="success";// Disable further dragging draggable.setAttribute('draggable','false');draggable.style.cursor="default";draggable.style.opacity="0.5";// Play success animation playSuccessAnimation();}else{// Incorrect answer messageDisplay.textContent=`❌ Oops! ${currentItem.name}is ${currentItem.type}waste.`;messageDisplay.className="error";}// Show next button for new round restartBtn.style.display="inline-block";}function playSuccessAnimation(){// Scale up score display briefly scoreDisplay.style.transform="scale(1.5)";setTimeout(()=>{scoreDisplay.style.transform="scale(1)";},200);}//==========INITIALIZATION==========// Start the game when page loads startGame();