// Environmental Impact Calculator // Impact data based on research and studies const impactData={// Base impact per pet type(annual averages)petTypes:{dog:{small:{food: 365,water: 73000,land: 0.15,carbon: 1100},medium:{food: 730,water: 146000,land: 0.3,carbon: 2200},large:{food: 1095,water: 219000,land: 0.45,carbon: 3300},'extra-large':{food: 1460,water: 292000,land: 0.6,carbon: 4400}},cat:{small:{food: 146,water: 29200,land: 0.06,carbon: 440},medium:{food: 219,water: 43800,land: 0.09,carbon: 660},large:{food: 292,water: 58400,land: 0.12,carbon: 880},'extra-large':{food: 365,water: 73000,land: 0.15,carbon: 1100}},bird:{small:{food: 73,water: 14600,land: 0.03,carbon: 220},medium:{food: 109,water: 21900,land: 0.045,carbon: 330},large:{food: 146,water: 29200,land: 0.06,carbon: 440},'extra-large':{food: 182,water: 36500,land: 0.075,carbon: 550}},fish:{small:{food: 36.5,water: 7300,land: 0.015,carbon: 110},medium:{food: 73,water: 14600,land: 0.03,carbon: 220},large:{food: 109,water: 21900,land: 0.045,carbon: 330},'extra-large':{food: 146,water: 29200,land: 0.06,carbon: 440}},'small-mammal':{small:{food: 73,water: 14600,land: 0.03,carbon: 220},medium:{food: 109,water: 21900,land: 0.045,carbon: 330},large:{food: 146,water: 29200,land: 0.06,carbon: 440},'extra-large':{food: 182,water: 36500,land: 0.075,carbon: 550}},reptile:{small:{food: 36.5,water: 7300,land: 0.015,carbon: 110},medium:{food: 73,water: 14600,land: 0.03,carbon: 220},large:{food: 109,water: 21900,land: 0.045,carbon: 330},'extra-large':{food: 146,water: 29200,land: 0.06,carbon: 440}}},// Food type multipliers foodTypes:{conventional:{carbon: 1.0,water: 1.0,land: 1.0},premium:{carbon: 1.3,water: 1.4,land: 1.2},raw:{carbon: 1.8,water: 2.0,land: 1.5},homemade:{carbon: 0.6,water: 0.7,land: 0.5},vegetarian:{carbon: 0.3,water: 0.4,land: 0.2}},// Transportation impact(per mile driven)transportation:{carbonPerMile: 0.404,// lbs CO2 per mile(average car)costPerMile: 0.58 // USD per mile}};// Initialize calculator document.addEventListener('DOMContentLoaded',function(){updateCalculator();});// Update calculator when inputs change function updateCalculator(){const petType=document.getElementById('pet-type').value;const petSize=document.getElementById('pet-size').value;const foodType=document.getElementById('food-type').value;const foodCost=parseFloat(document.getElementById('food-cost').value)|| 0;const vetMiles=parseFloat(document.getElementById('vet-miles').value)|| 0;if(!petType || !petSize){resetCalculatorDisplay();return;}// Get base impact data const baseImpact=impactData.petTypes[petType][petSize];// Apply food type multiplier let foodMultiplier=1.0;if(foodType){foodMultiplier=impactData.foodTypes[foodType];}// Calculate impacts const carbonImpact=(baseImpact.carbon * foodMultiplier.carbon)+(vetMiles * impactData.transportation.carbonPerMile);const waterImpact=baseImpact.water * foodMultiplier.water;const landImpact=baseImpact.land * foodMultiplier.land;const costImpact=foodCost+(vetMiles * impactData.transportation.costPerMile);// Update display updateImpactDisplay(carbonImpact,waterImpact,landImpact,costImpact);// Update comparison updateComparison(carbonImpact,waterImpact);}// Update impact display function updateImpactDisplay(carbon,water,land,cost){document.getElementById('carbon-impact').textContent=Math.round(carbon)+' lbs CO2';document.getElementById('water-impact').textContent=Math.round(water).toLocaleString()+' gallons';document.getElementById('land-impact').textContent=land.toFixed(2)+' acres';document.getElementById('cost-impact').textContent='$'+Math.round(cost);}// Update comparison text function updateComparison(carbonImpact,waterImpact){const comparisonText=document.getElementById('comparison-text');// Carbon comparisons let carbonComparison='';if(carbonImpact > 0){if(carbonImpact < 1000){carbonComparison=`Equivalent to driving ${Math.round(carbonImpact / 0.404)}miles in a car`;}else if(carbonImpact < 5000){carbonComparison=`Equivalent to the annual carbon footprint of ${Math.round(carbonImpact / 11000)}average Americans`;}else{carbonComparison=`Equivalent to ${Math.round(carbonImpact / 22000)}tons of CO2-like heating ${Math.round(carbonImpact / 12000)}homes for a year`;}}// Water comparisons let waterComparison='';if(waterImpact > 0){if(waterImpact < 50000){waterComparison=`Enough water to fill ${Math.round(waterImpact / 300)}Olympic-sized swimming pools`;}else{waterComparison=`Enough water for ${Math.round(waterImpact / 87600)}people to drink for a year`;}}if(carbonComparison && waterComparison){comparisonText.textContent=carbonComparison+'. '+waterComparison+'.';}else if(carbonComparison){comparisonText.textContent=carbonComparison+'.';}else if(waterComparison){comparisonText.textContent=waterComparison+'.';}else{comparisonText.textContent='Select options above to see comparisons';}}// Reset calculator display function resetCalculatorDisplay(){document.getElementById('carbon-impact').textContent='0 lbs CO2';document.getElementById('water-impact').textContent='0 gallons';document.getElementById('land-impact').textContent='0 acres';document.getElementById('cost-impact').textContent='$0';document.getElementById('comparison-text').textContent='Select options above to see comparisons';}// Animate percentage bars on scroll function animatePercentageBars(){const bars=document.querySelectorAll('.percentage-fill');bars.forEach(bar=>{const width=bar.style.width;bar.style.width='0%';setTimeout(()=>{bar.style.width=width;},500);});}// Intersection Observer for animations const observerOptions={threshold: 0.1,rootMargin: '0px 0px-50px 0px'};const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){if(entry.target.classList.contains('impact-breakdown')){animatePercentageBars();}entry.target.classList.add('animate-in');}});},observerOptions);// Observe elements for animation document.addEventListener('DOMContentLoaded',function(){const animatedElements=document.querySelectorAll('.impact-breakdown,.solutions-section,.comparison-section,.action-plan,.resources-section');animatedElements.forEach(element=>{observer.observe(element);});});// Add CSS for animations const style=document.createElement('style');style.textContent=` .animate-in{animation: fadeInUp 0.8s ease forwards;}@keyframes fadeInUp{from{opacity: 0;transform: translateY(30px);}to{opacity: 1;transform: translateY(0);}}`;document.head.appendChild(style);// Form validation and enhancement document.addEventListener('DOMContentLoaded',function(){// Add input validation const numberInputs=document.querySelectorAll('input[type="number"]');numberInputs.forEach(input=>{input.addEventListener('input',function(){if(this.value < 0){this.value=0;}});});// Add form submission prevention const forms=document.querySelectorAll('form');forms.forEach(form=>{form.addEventListener('submit',function(e){e.preventDefault();});});// Add loading animation for calculator const calculatorInputs=document.querySelectorAll('#pet-type,#pet-size,#food-type,#food-cost,#vet-miles');calculatorInputs.forEach(input=>{input.addEventListener('change',function(){const results=document.querySelector('.calculator-results');results.style.opacity='0.7';setTimeout(()=>{results.style.opacity='1';},300);});});});// Export calculator data for potential use in other scripts window.environmentalImpactData=impactData;