//=====INITIALIZATION=====document.addEventListener('DOMContentLoaded',function(){initializeFeedingCalculator();});//=====MAIN INITIALIZATION FUNCTION=====function initializeFeedingCalculator(){// Initialize AOS animations initializeAOS();// Initialize theme toggle functionality initializeThemeToggle();// Initialize feeding calculator initializeFeedingCalculatorLogic();}//=====ANIMATION INITIALIZATION=====function initializeAOS(){AOS.init({duration: 800,once: true});}//=====THEME MANAGEMENT=====function initializeThemeToggle(){const themeToggle=document.getElementById('themeToggle');const body=document.body;// Load saved theme preference(default is dark theme)if(localStorage.getItem('theme')==='light'){body.classList.add('light-theme');}// Theme toggle click handler themeToggle.addEventListener('click',()=>{// Toggle light theme class body.classList.toggle('light-theme');// Save user preference if(body.classList.contains('light-theme')){localStorage.setItem('theme','light');}else{localStorage.setItem('theme','dark');}});}//=====FEEDING DATA=====const feedingData={bird:{small: "15-20g seeds(Millet/Sunflower). Clean water.",medium: "30g Mixed Seeds+Fruit chunks.",large: "N/A",xlarge: "N/A"},squirrel:{small: "20g nuts(unsalted)& fruit.",medium: "40g corn,apple,walnuts.",large: "N/A",xlarge: "N/A"},cat:{small: "80g Wet Food / 30g Dry Food.",medium: "150g Wet Food / 50g Dry Food.",large: "200g+Wet Food.",xlarge: "N/A"},dog:{small: "100g Balanced Meal.",medium: "300g Balanced Meal.",large: "500g Balanced Meal.",xlarge: "800g+Balanced Meal."}};//=====CALCULATOR LOGIC=====function initializeFeedingCalculatorLogic(){const calculateBtn=document.getElementById('calculateBtn');const resultBox=document.getElementById('resultBox');const resultContent=document.getElementById('resultContent');// Calculate button click handler calculateBtn.addEventListener('click',()=>{const animalType=document.getElementById('animalType').value;const animalSize=document.getElementById('animalSize').value;// Validate input selection if(!animalType || !animalSize){// Visual feedback for invalid input showValidationError(calculateBtn);return;}// Hide current result and prepare for new calculation resultBox.classList.remove('show');// Calculate and display result with animation delay setTimeout(()=>{const result=calculateFeedingRecommendation(animalType,animalSize);displayResult(result);},100);});}//=====CALCULATION FUNCTIONS=====function calculateFeedingRecommendation(type,size){// Check if data exists for the selected combination if(feedingData[type] && feedingData[type][size]){return feedingData[type][size];}// Return default message for unavailable combinations return "Data unavailable for this combination.";}//=====RESULT DISPLAY=====function displayResult(result){const resultBox=document.getElementById('resultBox');const resultContent=document.getElementById('resultContent');// Update result content resultContent.innerHTML=`<strong>Recommendation:</strong><br>${result}`;// Show result with animation resultBox.classList.add('show');}//=====VALIDATION FEEDBACK=====function showValidationError(button){// Simple shake animation for user feedback button.style.transform="translateX(5px)";setTimeout(()=>{button.style.transform="translateX(0)";},100);}