//==========GAME DATA==========const animals=[ // Carnivores(meat eaters){name: "Lion",emoji: "🦁",type: "carn"},{name: "Tiger",emoji: "🐯",type: "carn"},{name: "Leopard",emoji: "🐆",type: "carn"},{name: "Wolf",emoji: "🐺",type: "carn"},{name: "Fox",emoji: "🦊",type: "carn"},{name: "Crocodile",emoji: "🐊",type: "carn"},{name: "Snake",emoji: "🐍",type: "carn"},{name: "Eagle",emoji: "🦅",type: "carn"},{name: "Owl",emoji: "🦉",type: "carn"},{name: "Shark",emoji: "🦈",type: "carn"},// Herbivores(plant eaters){name: "Cow",emoji: "🐄",type: "herb"},{name: "Goat",emoji: "🐐",type: "herb"},{name: "Deer",emoji: "🦌",type: "herb"},{name: "Horse",emoji: "🐎",type: "herb"},{name: "Elephant",emoji: "🐘",type: "herb"},{name: "Rabbit",emoji: "🐇",type: "herb"},{name: "Giraffe",emoji: "🦒",type: "herb"},{name: "Panda",emoji: "🐼",type: "herb"},{name: "Camel",emoji: "🐫",type: "herb"},{name: "Sheep",emoji: "🐑",type: "herb"},// Omnivores(eat both plants and meat){name: "Bear",emoji: "🐻",type: "omni"},{name: "Monkey",emoji: "🐵",type: "omni"},{name: "Pig",emoji: "🐷",type: "omni"},{name: "Dog",emoji: "🐕",type: "omni"},{name: "Cat",emoji: "🐈",type: "omni"},{name: "Crow",emoji: "🐦‍⬛",type: "omni"},{name: "Hen",emoji: "🐔",type: "omni"},{name: "Duck",emoji: "🦆",type: "omni"},{name: "Rat",emoji: "🐀",type: "omni"},{name: "Human",emoji: "🧑",type: "omni"}];//==========GAME STATE==========let currentIndex=0;let correctAnswers=0;let wrongAnswers=0;let timeRemaining=60;let gameTimer;//==========DOM ELEMENTS==========const animalElement=document.getElementById("animal");const animalNameElement=document.getElementById("animalName");const correctSound=document.getElementById("correctSound");const wrongSound=document.getElementById("wrongSound");const timerElement=document.getElementById("time");//==========INITIALIZATION==========document.addEventListener('DOMContentLoaded',function(){// Shuffle animals for random order shuffleArray(animals);// Set up drag and drop functionality setupDragAndDrop();// Start the game timer startTimer();// Load first animal loadNextAnimal();console.log('🐾 Animal Classification Game Started');});//==========GAME LOGIC==========function loadNextAnimal(){if(currentIndex >=animals.length){endGame();return;}const currentAnimal=animals[currentIndex];animalElement.textContent=currentAnimal.emoji;animalNameElement.textContent=currentAnimal.name;animalElement.style.opacity="1";// Add visual entrance animation animalElement.style.transform="scale(0.8)";setTimeout(()=>{animalElement.style.transform="scale(1)";},100);}function setupDragAndDrop(){// Animal drag events animalElement.addEventListener("dragstart",function(){this.classList.add("dragging");});animalElement.addEventListener("dragend",function(){this.classList.remove("dragging");});// Drop zone events document.querySelectorAll(".box").forEach(box=>{box.addEventListener("dragover",function(e){e.preventDefault();this.classList.add("drag-over");});box.addEventListener("dragleave",function(){this.classList.remove("drag-over");});box.addEventListener("drop",function(e){e.preventDefault();this.classList.remove("drag-over");checkAnswer(this.dataset.type,this);});});}function checkAnswer(selectedType,dropBox){const currentAnimal=animals[currentIndex];const isCorrect=selectedType===currentAnimal.type;// Clear previous visual feedback document.querySelectorAll(".box").forEach(box=> box.classList.remove("correct","wrong","drag-over"));if(isCorrect){correctAnswers++;dropBox.classList.add("correct");playSound(correctSound);}else{wrongAnswers++;dropBox.classList.add("wrong");playSound(wrongSound);}// Remove visual feedback after delay setTimeout(()=>{dropBox.classList.remove("correct","wrong");},1000);// Hide current animal and load next animalElement.style.opacity="0";setTimeout(()=>{currentIndex++;loadNextAnimal();},800);}function playSound(sound){if(sound){sound.currentTime=0;sound.play();// Stop sound after 1 second setTimeout(()=>{sound.pause();sound.currentTime=0;},1000);}}//==========TIMER SYSTEM==========function startTimer(){gameTimer=setInterval(()=>{timeRemaining--;// Format time as MM:SS const minutes=String(Math.floor(timeRemaining / 60)).padStart(2,"0");const seconds=String(timeRemaining % 60).padStart(2,"0");timerElement.textContent=`${minutes}:${seconds}`;// End game when time runs out if(timeRemaining <=0){clearInterval(gameTimer);endGame();}},1000);}//==========GAME END==========function endGame(){clearInterval(gameTimer);// Show results modal const modal=document.getElementById("modal");if(modal){modal.style.display="flex";// Update score display const correctCountEl=document.getElementById("correctCount");const wrongCountEl=document.getElementById("wrongCount");if(correctCountEl)correctCountEl.textContent=correctAnswers;if(wrongCountEl)wrongCountEl.textContent=wrongAnswers;}// Play completion sound playSound(correctSound);console.log(`Game Over! Correct: ${correctAnswers},Wrong: ${wrongAnswers}`);}//==========NAVIGATION==========function restart(){location.reload();}function goBack(){window.history.back();}//==========UTILITY FUNCTIONS==========function shuffleArray(array){for(let i=array.length-1;i > 0;i--){const j=Math.floor(Math.random()*(i+1));[array[i],array[j]]=[array[j],array[i]];}}//==========CONSOLE LOGGING==========console.log("%c🐾 Animal Classification Game","font-size: 18px;font-weight: bold;color: #2e7d32;");console.log("%cLearn about carnivores,herbivores,and omnivores through fun drag-and-drop gameplay!","font-size: 14px;color: #666;");