//=====PROFILE MANAGER CLASS=====class ProfileManager{constructor(){this.init();}init(){this.setupProfileEditing();this.setupTabNavigation();this.setupPasswordChange();}//=====PROFILE EDITING=====setupProfileEditing(){// Get DOM elements const editBtn=document.getElementById('editBtn');const usernameDisplay=document.getElementById('usernameDisplay');const emailDisplay=document.getElementById('emailDisplay');const usernameInput=document.getElementById('usernameInput');const emailInput=document.getElementById('emailInput');const profilePic=document.getElementById('profilePic');// Initialize input values usernameInput.value=usernameDisplay.textContent;emailInput.value=emailDisplay.textContent;let editing=false;// Edit/Save button click handler editBtn.addEventListener('click',()=>{if(!editing){// Enter edit mode this.enterEditMode(usernameDisplay,emailDisplay,usernameInput,emailInput,editBtn);editing=true;}else{// Save changes and exit edit mode this.saveProfileChanges(usernameDisplay,emailDisplay,usernameInput,emailInput,editBtn);editing=false;}});// Profile picture change handler profilePic.addEventListener('click',()=>{this.changeProfilePicture(profilePic);});}enterEditMode(usernameDisplay,emailDisplay,usernameInput,emailInput,editBtn){// Hide display elements,show input elements usernameDisplay.style.display='none';emailDisplay.style.display='none';usernameInput.style.display='block';emailInput.style.display='block';// Update button text editBtn.textContent='Save Changes';}saveProfileChanges(usernameDisplay,emailDisplay,usernameInput,emailInput,editBtn){// Update display values usernameDisplay.textContent=usernameInput.value;emailDisplay.textContent=emailInput.value;// Hide input elements,show display elements usernameDisplay.style.display='block';emailDisplay.style.display='block';usernameInput.style.display='none';emailInput.style.display='none';// Update button text editBtn.textContent='Edit Profile';// Log changes(in production,this would be an API call)console.log('Profile saved:',{username: usernameDisplay.textContent,email: emailDisplay.textContent});}changeProfilePicture(profilePic){const url=prompt('Enter new profile picture URL:');if(url && url.trim()){profilePic.src=url.trim();}}//=====TAB NAVIGATION=====setupTabNavigation(){const tabButtons=document.querySelectorAll('.tab-btn');const tabPanes=document.querySelectorAll('.tab-pane');tabButtons.forEach(button=>{button.addEventListener('click',()=>{// Remove active class from all buttons and panes tabButtons.forEach(btn=> btn.classList.remove('active'));tabPanes.forEach(pane=> pane.classList.remove('active'));// Add active class to clicked button button.classList.add('active');// Show corresponding tab pane const tabId=button.getAttribute('data-tab');const targetPane=document.getElementById(tabId);if(targetPane){targetPane.classList.add('active');}});});}//=====PASSWORD MANAGEMENT=====setupPasswordChange(){const changePasswordBtn=document.querySelector('.change-password-btn');if(changePasswordBtn){changePasswordBtn.addEventListener('click',()=>{this.handlePasswordChange();});}}handlePasswordChange(){// Get password input elements const currentPassword=document.querySelector('.password-section input[placeholder="Current Password"]');const newPassword=document.querySelector('.password-section input[placeholder="New Password"]');const confirmPassword=document.querySelector('.password-section input[placeholder="Confirm New Password"]');// Validate all fields are filled if(!currentPassword.value || !newPassword.value || !confirmPassword.value){alert('Please fill in all password fields.');return;}// Validate new passwords match if(newPassword.value !==confirmPassword.value){alert('New passwords do not match.');return;}// Validate password strength(basic check)if(newPassword.value.length < 8){alert('New password must be at least 8 characters long.');return;}// In production,this would be an API call to change password alert('Password changed successfully!');// Clear form fields currentPassword.value='';newPassword.value='';confirmPassword.value='';}}//=====PROFILE NAVIGATION UTILITIES=====function handleProfileChange(value){switch(value){case 'community': window.location.href='./community/';break;case 'logout': // Handle logout logic alert('Logging out...');// In production,this would clear session/token and redirect window.location.href='./login.html';break;case 'profile': window.location.href='./profile.html';break;default: console.warn('Unknown profile navigation option:',value);}}//=====INITIALIZATION=====document.addEventListener('DOMContentLoaded',()=>{new ProfileManager();});