//=====QUIZ QUESTION DATABASE=====const questions=[{q: "Why should we plant trees?",o: ["Decoration","More oxygen","Noise","Waste"],a: 1},{q: "Which gas causes global warming?",o: ["Oxygen","Carbon dioxide","Nitrogen","Helium"],a: 1},{q: "Best way to save water?",o: ["Leave taps open","Fix leaks","Waste water","Ignore"],a: 1},{q: "Which helps reduce pollution?",o: ["Burning waste","Planting trees","Cutting forests","Throwing trash"],a: 1},{q: "Which is eco-friendly transport?",o: ["Car","Bike","Cycle","Plane"],a: 2},{q: "Why recycle waste?",o: ["Increase trash","Save resources","Pollute air","Waste money"],a: 1},{q: "Which energy is renewable?",o: ["Coal","Solar","Oil","Gas"],a: 1},{q: "What harms oceans the most?",o: ["Clean water","Plastic waste","Fish","Sand"],a: 1},{q: "What should we do with lights?",o: ["Keep on","Switch off","Break","Ignore"],a: 1},{q: "Clean environment means?",o: ["Healthy life","More disease","Dirty water","Less oxygen"],a: 0}];//=====QUIZ STATE MANAGEMENT=====let index=0;// Current question index let score=0;// Correct answers count let seconds=120;// 2-minute time limit let timer=null;// Timer interval reference //=====QUIZ INITIALIZATION=====function startQuiz(){// Transition screens startScreen.style.display="none";quizScreen.style.display="block";// Load first question and start timer loadQuestion();startTimer();}//=====TIMER MANAGEMENT=====function startTimer(){updateTime();timer=setInterval(()=>{seconds--;updateTime();if(seconds <=0){clearInterval(timer);showResult();}},1000);}function updateTime(){let minutes=Math.floor(seconds / 60);let secs=seconds % 60;time.textContent=`${minutes}:${secs < 10 ? '0' : ''}${secs}`;}//=====QUESTION DISPLAY=====function loadQuestion(){let currentQuestion=questions[index];// Update question text with number question.textContent=`Q${index+1}. ${currentQuestion.q}`;// Clear previous options and create new ones options.innerHTML="";currentQuestion.o.forEach((option,optionIndex)=>{let optionDiv=document.createElement("div");optionDiv.className="option";optionDiv.textContent=option;optionDiv.onclick=()=> selectOption(optionDiv,optionIndex);options.appendChild(optionDiv);});}//=====ANSWER SELECTION=====function selectOption(element,optionIndex){// Remove previous selection highlight document.querySelectorAll(".option").forEach(option=> option.classList.remove("selected"));// Highlight selected option element.classList.add("selected");// Store correctness data for validation element.dataset.correct=(optionIndex===questions[index].a).toString();}//=====QUESTION NAVIGATION=====function nextQuestion(){let selectedOption=document.querySelector(".option.selected");// Check if user has selected an answer if(!selectedOption){alert("Please select an option 😊");return;}// Check answer and update score if(selectedOption.dataset.correct==="true"){score++;}// Move to next question or show results index++;if(index < questions.length){loadQuestion();}else{showResult();}}//=====RESULTS DISPLAY=====function showResult(){// Stop timer clearInterval(timer);// Transition to results screen quizScreen.style.display="none";resultScreen.style.display="block";// Display score scoreEl.textContent=`${score}/${questions.length}`;// Show performance-based remark if(score >=8){remark.textContent="🌟 Eco Champion!";}else if(score >=5){remark.textContent="👍 Good effort!";}else{remark.textContent="🌱 Keep learning!";}}//=====DOM ELEMENT REFERENCES=====const scoreEl=document.getElementById("score");