//=====DOM ELEMENT REFERENCES=====const filterBtns=document.querySelectorAll(".filter-btn");const cards=document.querySelectorAll(".card");//=====FILTER SYSTEM=====filterBtns.forEach(btn=>{btn.addEventListener("click",()=>{// Remove active class from all filter buttons filterBtns.forEach(b=> b.classList.remove("active"));// Add active class to clicked button btn.classList.add("active");// Get the filter category from button's data attribute const filter=btn.dataset.filter;// Show/hide cards based on selected filter cards.forEach(card=>{const shouldShow=filter==="all" || card.dataset.category===filter;card.style.display=shouldShow ? "block" : "none";});});});