//=====QUIZ QUESTION DATABASE=====const questions=[{q: "Where should vegetable peels go?",o: ["Dry Waste","Wet Waste","Plastic","Glass"],a: 1},{q: "Which item is recyclable?",o: ["Food waste","Plastic bottle","Used tissue","Leaves"],a: 1},{q: "Where does broken glass go?",o: ["Wet","Dry","Hazardous","Plastic"],a: 2},{q: "Best way to reduce waste?",o: ["Burn it","Reuse items","Throw away","Ignore"],a: 1},{q: "Which bin for paper?",o: ["Wet","Dry","Hazardous","Medical"],a: 1},{q: "Plastic bags harm because?",o: ["They dissolve","They pollute","They help soil","They vanish"],a: 1},{q: "What is composting?",o: ["Burning waste","Recycling plastic","Turning waste to manure","Dumping trash"],a: 2},{q: "Used batteries go to?",o: ["Wet","Dry","Hazardous","Recycle bin"],a: 2},{q: "Which helps environment?",o: ["Littering","Segregation","Burning trash","Plastic use"],a: 1},{q: "Wet waste mainly comes from?",o: ["Kitchen","Bathroom","Road","Factory"],a: 0}];//=====QUIZ STATE MANAGEMENT=====let index=0;// Current question index let score=0;// Correct answers count let time=180;// 3 minutes in seconds let timer=null;// Timer interval reference let answers=[];// User's selected answers //=====PROGRESS PERSISTENCE=====const PROGRESS_KEY='wasteManagementQuizProgress';function saveProgress(){const progress={currentIndex: index,answers: answers,score: score,remainingTime: time,timestamp: Date.now(),quizId: 'waste-management-quiz'};localStorage.setItem(PROGRESS_KEY,JSON.stringify(progress));}function loadProgress(){const saved=localStorage.getItem(PROGRESS_KEY);if(saved){const progress=JSON.parse(saved);index=progress.currentIndex || 0;answers=progress.answers || [];score=progress.score || 0;time=progress.remainingTime || 180;return true;}return false;}function clearProgress(){localStorage.removeItem(PROGRESS_KEY);}//=====DOM ELEMENT REFERENCES=====const startScreen=document.getElementById('startScreen');const quizScreen=document.getElementById('quizScreen');const resultScreen=document.getElementById('resultScreen');const question=document.getElementById('question');const options=document.getElementById('options');const remark=document.getElementById('remark');//=====QUIZ INITIALIZATION=====function initializeQuiz(){// Check for existing progress on page load if(loadProgress()){const resumeSection=document.getElementById('resumeSection');if(resumeSection){resumeSection.style.display='block';}}}// Call initialization initializeQuiz();function startQuiz(){// Clear any existing progress when starting new quiz clearProgress();// Reset quiz state index=0;score=0;time=180;answers=new Array(questions.length).fill(null);// Transition screens startScreen.style.display="none";quizScreen.style.display="block";// Load first question and start timer loadQuestion();startTimer();}//=====TIMER MANAGEMENT=====function startTimer(){updateTime();timer=setInterval(()=>{time--;updateTime();if(time <=0){clearInterval(timer);showResult();}},1000);}function updateTime(){let minutes=Math.floor(time / 60);let seconds=time % 60;document.getElementById("time").textContent=`${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;}//=====QUESTION DISPLAY=====function loadQuestion(){let currentQuestion=questions[index];// Update progress text const progressText=document.querySelector('.progress-metrics span:first-child');if(progressText){const timeSpent=180-time;// Total time minus remaining time progressText.textContent=`Time Spent: ${timeSpent}s`;}// Update questions completed const questionsCompleted=document.querySelector('.progress-metrics span:last-child');if(questionsCompleted){questionsCompleted.textContent=`Completed: ${index+1}/10`;}// Update progress bar const progressFill=document.getElementById('progressFill');if(progressFill){const progressPercent=((index+1)/ questions.length)* 100;progressFill.style.width=`${progressPercent}%`;}// Display question with numbering question.textContent=`Q${index+1}. ${currentQuestion.q}`;// Clear previous options and create new ones options.innerHTML="";// Create option elements with FontAwesome trash icons currentQuestion.o.forEach((optionText,optionIndex)=>{let optionDiv=document.createElement("div");optionDiv.className="option";// Add trash icon and option text optionDiv.innerHTML=`<i class="fa-solid fa-trash"></i> ${optionText}`;// Click handler for option selection optionDiv.onclick=()=> selectOption(optionDiv,optionIndex);// Restore previous selection if navigating back if(answers[index]===optionIndex){optionDiv.classList.add("selected");}options.appendChild(optionDiv);});}//=====ANSWER SELECTION=====function selectOption(element,optionIndex){// Remove previous selection highlighting document.querySelectorAll(".option").forEach(option=> option.classList.remove("selected"));// Highlight selected option element.classList.add("selected");// Store user's answer answers[index]=optionIndex;// Store correctness data for validation element.dataset.correct=(optionIndex===questions[index].a).toString();// Save progress after each answer selection saveProgress();}//=====QUESTION NAVIGATION=====function nextQuestion(){let selectedOption=document.querySelector(".option.selected");// Require answer selection with friendly alert if(!selectedOption){alert("Please select an option 😊");return;}// Update score if answer was correct if(selectedOption.dataset.correct==="true"){score++;}// Save progress after moving to next question saveProgress();// Move to next question or show results index++;if(index < questions.length){loadQuestion();}else{showResult();}}//=====QUIZ RESUME FUNCTIONALITY=====function resumeSavedQuiz(){if(loadProgress()){// Transition to quiz screen startScreen.style.display="none";quizScreen.style.display="block";// Load current question and resume timer loadQuestion();startTimer();}}function pauseQuiz(){// Stop the timer clearInterval(timer);timer=null;// Save progress saveProgress();// Show resume button and hide pause button document.getElementById('pauseBtn').style.display='none';document.getElementById('resumeBtn').style.display='inline-block';// Optional: Show a pause message alert("Quiz paused! Click resume to continue.");}function resumeCurrentQuiz(){// Hide resume button and show pause button document.getElementById('resumeBtn').style.display='none';document.getElementById('pauseBtn').style.display='inline-block';// Restart timer startTimer();}//=====RESULTS DISPLAY=====function showResult(){// Stop timer and clear saved progress clearInterval(timer);clearProgress();// Transition to results screen quizScreen.style.display="none";resultScreen.style.display="block";// Display final score document.getElementById("score").textContent=`${score}/${questions.length}`;// Show performance-based remark with emojis if(score >=8){remark.textContent="🌟 Waste Warrior!";}else if(score >=5){remark.textContent="👍 Good Effort!";}else{remark.textContent="🌱 Keep Learning!";}}