//=====AOS INITIALIZATION=====AOS.init({duration: 800,// Animation duration in milliseconds once: true,// Animation occurs only once per element});//=====THEME TOGGLE SYSTEM=====const themeToggle=document.getElementById('themeToggle');const prefersDarkScheme=window.matchMedia('(prefers-color-scheme: dark)');function initializeThemeToggle(){// Check for saved theme preference in localStorage const currentTheme=localStorage.getItem('theme');// Apply dark theme if user previously selected it or if system prefers dark and no preference saved if(currentTheme==='dark' ||(!currentTheme && prefersDarkScheme.matches)){document.body.classList.add('dark-theme');themeToggle.classList.add('dark');}// Add click event listener for theme toggle themeToggle.addEventListener('click',()=>{// Toggle dark theme class on body document.body.classList.toggle('dark-theme');// Toggle dark class on toggle button themeToggle.classList.toggle('dark');// Save theme preference to localStorage if(document.body.classList.contains('dark-theme')){localStorage.setItem('theme','dark');}else{localStorage.setItem('theme','light');}});}//=====INITIALIZATION=====document.addEventListener('DOMContentLoaded',function(){initializeThemeToggle();});