// Volunteer Opportunities Page JavaScript document.addEventListener('DOMContentLoaded',function(){initializeVolunteerOpportunities();});function initializeVolunteerOpportunities(){// Initialize components setupSearchFunctionality();setupFilterButtons();setupQuickFilters();loadOpportunities();setupModal();setupNewsletter();// Load user's location if available loadUserLocation();}// Sample opportunities data const opportunitiesData=[{id: 'wildlife-sanctuary-volunteer',title: 'Wildlife Sanctuary Assistant',organization: 'Pacific Wildlife Sanctuary',type: 'conservation',commitment: '3-6 months',location: 'San Diego,CA',remote: false,description: 'Help care for rescued wildlife including birds,mammals,and reptiles. Assist with feeding,cleaning enclosures,and educational programs.',requirements: ['18+years old','Valid driver\'s license','Weekend availability','Background check required'],skills: ['Animal care','Cleaning','Public speaking','Physical stamina'],contact: 'volunteer@pacificwildlife.org',featured: true,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <div class="overview-section"> <h4>About This Opportunity</h4> <p>Our wildlife sanctuary rescues and rehabilitates injured and orphaned wildlife,with the goal of releasing them back into their natural habitats. As a volunteer assistant,you'll play a crucial role in animal care and public education.</p> <h4>Daily Responsibilities</h4> <ul> <li>Prepare and distribute food for various animal species</li> <li>Clean and maintain animal enclosures</li> <li>Assist with medical treatments under supervision</li> <li>Lead educational tours for visitors</li> <li>Help with administrative tasks</li> </ul> </div> <div class="overview-section"> <h4>Requirements & Qualifications</h4> <ul> <li>Minimum age: 18 years</li> <li>Valid California driver's license</li> <li>Ability to lift 50+pounds</li> <li>Commitment to at least 3 months</li> <li>Successful completion of background check</li> </ul> <h4>Training Provided</h4> <ul> <li>Comprehensive orientation program</li> <li>Animal handling and safety training</li> <li>CPR and first aid certification</li> <li>Ongoing mentorship and support</li> </ul> </div> <div class="overview-section"> <h4>Benefits & Impact</h4> <ul> <li>Hands-on wildlife conservation experience</li> <li>Professional development opportunities</li> <li>Networking with conservation professionals</li> <li>Direct impact on wildlife recovery</li> <li>Letter of recommendation available</li> </ul> </div> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('wildlife-sanctuary-volunteer')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('wildlife-sanctuary-volunteer')">Save for Later</button> <button class="btn-secondary" onclick="shareOpportunity('wildlife-sanctuary-volunteer')">Share</button> </div> </div> `},{id: 'animal-shelter-weekend',title: 'Weekend Shelter Volunteer',organization: 'City Animal Services',type: 'shelter',commitment: 'Weekends',location: 'Multiple locations',remote: false,description: 'Perfect for busy professionals! Help with animal care,playtime,and adoption events during weekends.',requirements: ['16+years old','Weekend availability'],skills: ['Animal handling','Customer service'],contact: 'volunteer@cityanimals.org',featured: true,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Perfect for Busy Schedules</h4> <p>Our weekend volunteer program is designed for people with weekday commitments. Help make a difference in animals' lives while maintaining your work-life balance.</p> <h4>Available Shifts</h4> <ul> <li>Saturday mornings: 9 AM-12 PM</li> <li>Saturday afternoons: 1 PM-4 PM</li> <li>Sunday mornings: 10 AM-1 PM</li> <li>Sunday afternoons: 2 PM-5 PM</li> </ul> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('animal-shelter-weekend')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('animal-shelter-weekend')">Save for Later</button> </div> </div> `},{id: 'marine-conservation',title: 'Marine Conservation Volunteer',organization: 'Ocean Guardians',type: 'conservation',commitment: 'Flexible',location: 'Coastal areas',remote: false,description: 'Join beach cleanups,sea turtle monitoring,and marine education programs to protect our oceans.',requirements: ['16+years old','Transportation to coast'],skills: ['Environmental education','Data collection'],contact: 'volunteer@oceanguardians.org',featured: true,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Protect Our Oceans</h4> <p>Marine conservation volunteers play a vital role in protecting coastal ecosystems and marine wildlife. Our programs focus on research,education,and direct conservation action.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('marine-conservation')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('marine-conservation')">Save for Later</button> </div> </div> `},{id: 'virtual-animal-education',title: 'Virtual Animal Education Assistant',organization: 'Global Animal Education Network',type: 'education',commitment: 'Flexible',location: 'Remote',remote: true,description: 'Help create and moderate online educational content about animal welfare and conservation.',requirements: ['Computer access','Good communication skills'],skills: ['Content creation','Social media','Teaching'],contact: 'education@globalanimal.net',featured: false,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Work from Anywhere</h4> <p>Perfect for volunteers who want to make an impact without leaving home. Help educate people worldwide about animal welfare through digital content.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('virtual-animal-education')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('virtual-animal-education')">Save for Later</button> </div> </div> `},{id: 'farm-animal-rescue',title: 'Farm Animal Rescue Volunteer',organization: 'Farm Sanctuary',type: 'rescue',commitment: 'Ongoing',location: 'Rural areas',remote: false,description: 'Assist with the care and rehabilitation of rescued farm animals at our sanctuary.',requirements: ['18+years old','Physical fitness'],skills: ['Animal husbandry','Farm work'],contact: 'volunteer@farmsanctuary.org',featured: false,urgent: true,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Compassionate Farm Animal Care</h4> <p>Our sanctuary provides lifelong care for rescued farm animals. Volunteers help maintain their quality of life and advocate for farm animal welfare.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('farm-animal-rescue')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('farm-animal-rescue')">Save for Later</button> </div> </div> `},{id: 'youth-conservation-program',title: 'Youth Conservation Program Leader',organization: 'Young Conservationists',type: 'education',commitment: 'Short-term',location: 'Various locations',remote: false,description: 'Lead environmental education programs for children and teens,teaching about wildlife conservation.',requirements: ['18+years old','Experience with youth','Background in education'],skills: ['Teaching','Environmental education','Youth mentoring'],contact: 'youth@youngconservationists.org',featured: false,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Inspire the Next Generation</h4> <p>Lead engaging educational programs that teach children about wildlife conservation,environmental stewardship,and animal welfare.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('youth-conservation-program')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('youth-conservation-program')">Save for Later</button> </div> </div> `},{id: 'emergency-animal-response',title: 'Emergency Animal Response Team',organization: 'Animal Emergency Services',type: 'rescue',commitment: 'On-call',location: 'Regional',remote: false,description: 'Respond to animal emergencies including natural disasters,cruelty cases,and urgent rescue situations.',requirements: ['21+years old','Emergency training','Available 24/7'],skills: ['Emergency response','Animal handling','Crisis management'],contact: 'emergency@animalemergencies.org',featured: false,urgent: true,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Critical Emergency Response</h4> <p>Be part of our rapid response team that handles animal emergencies,from natural disasters to cruelty interventions.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('emergency-animal-response')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('emergency-animal-response')">Save for Later</button> </div> </div> `},{id: 'wildlife-rehabilitation',title: 'Wildlife Rehabilitation Assistant',organization: 'Wildlife Recovery Center',type: 'conservation',commitment: 'Seasonal',location: 'Forest areas',remote: false,description: 'Assist licensed rehabilitators with the care and treatment of injured wildlife.',requirements: ['18+years old','Wildlife rehabilitation course','Physical ability'],skills: ['Wildlife handling','Medical assistance','Record keeping'],contact: 'rehab@wildliferecovery.org',featured: false,urgent: false,content: ` <div class="opportunity-detail"> <div class="opportunity-overview"> <h4>Specialized Wildlife Care</h4> <p>Work alongside licensed wildlife rehabilitators to provide specialized care for injured and orphaned wildlife.</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="applyForOpportunity('wildlife-rehabilitation')">Apply Now</button> <button class="btn-secondary" onclick="saveOpportunity('wildlife-rehabilitation')">Save for Later</button> </div> </div> `}];let currentFilter='all';let currentQuickFilters=[];let searchQuery='';let locationQuery='';let displayedOpportunities=12;function setupSearchFunctionality(){const searchInput=document.getElementById('volunteer-search');const locationInput=document.getElementById('location-search');searchInput.addEventListener('input',function(e){searchQuery=e.target.value.toLowerCase();filterOpportunities();});locationInput.addEventListener('input',function(e){locationQuery=e.target.value.toLowerCase();filterOpportunities();});}function setupFilterButtons(){const filterButtons=document.querySelectorAll('.filter-btn');filterButtons.forEach(button=>{button.addEventListener('click',function(){// Remove active class from all buttons filterButtons.forEach(btn=> btn.classList.remove('active'));// Add active class to clicked button this.classList.add('active');// Update current filter currentFilter=this.dataset.filter;filterOpportunities();});});}function setupQuickFilters(){const quickFilterButtons=document.querySelectorAll('.quick-filter-btn');quickFilterButtons.forEach(button=>{button.addEventListener('click',function(){const filterType=this.dataset.type;// Toggle active state this.classList.toggle('active');// Update quick filters array if(this.classList.contains('active')){currentQuickFilters.push(filterType);}else{currentQuickFilters=currentQuickFilters.filter(f=> f !==filterType);}filterOpportunities();});});}function filterOpportunities(){let filteredOpportunities=opportunitiesData.filter(opportunity=>{// Filter by type const typeMatch=currentFilter==='all' || opportunity.type===currentFilter;// Filter by search query const searchMatch=searchQuery==='' || opportunity.title.toLowerCase().includes(searchQuery)|| opportunity.description.toLowerCase().includes(searchQuery)|| opportunity.organization.toLowerCase().includes(searchQuery);// Filter by location const locationMatch=locationQuery==='' || opportunity.location.toLowerCase().includes(locationQuery);// Filter by quick filters let quickMatch=true;if(currentQuickFilters.length > 0){quickMatch=currentQuickFilters.every(filter=>{switch(filter){case 'remote': return opportunity.remote;case 'weekend': return opportunity.commitment.toLowerCase().includes('weekend');case 'short-term': return opportunity.commitment.toLowerCase().includes('short')|| opportunity.commitment.toLowerCase().includes('flexible');case 'ongoing': return opportunity.commitment.toLowerCase().includes('ongoing')|| !opportunity.commitment.toLowerCase().includes('short');case 'youth': return opportunity.requirements.some(req=> req.toLowerCase().includes('youth')|| req.toLowerCase().includes('children')|| req.toLowerCase().includes('teens'));default: return true;}});}return typeMatch && searchMatch && locationMatch && quickMatch;});displayOpportunities(filteredOpportunities);updateResultsCount(filteredOpportunities.length);}function displayOpportunities(opportunities){const opportunitiesGrid=document.getElementById('opportunities-grid');opportunitiesGrid.innerHTML='';const opportunitiesToShow=opportunities.slice(0,displayedOpportunities);opportunitiesToShow.forEach((opportunity,index)=>{const opportunityCard=createOpportunityCard(opportunity);opportunityCard.style.animationDelay=`${index * 0.1}s`;opportunitiesGrid.appendChild(opportunityCard);});// Show/hide load more button const loadMoreBtn=document.getElementById('load-more-btn');if(opportunities.length > displayedOpportunities){loadMoreBtn.style.display='block';loadMoreBtn.onclick=()=> loadMoreOpportunities(opportunities);}else{loadMoreBtn.style.display='none';}}function createOpportunityCard(opportunity){const card=document.createElement('div');card.className='opportunity-card';card.onclick=()=> viewOpportunity(opportunity.id);const urgentBadge=opportunity.urgent ? '<span class="urgent-badge">Urgent</span>' : '';card.innerHTML=` <div class="opportunity-header"> <h3>${opportunity.title}${urgentBadge}</h3> <p class="opportunity-org">${opportunity.organization}</p> <div class="opportunity-meta"> <span class="opportunity-type ${opportunity.type}">${getTypeName(opportunity.type)}</span> <span class="opportunity-commitment">${opportunity.commitment}</span> </div> <div class="opportunity-location"> <i class="fas fa-map-marker-alt"></i> <span>${opportunity.location}</span> </div> </div> <div class="opportunity-description"> <p>${opportunity.description}</p> </div> <div class="opportunity-actions"> <button class="btn-primary" onclick="event.stopPropagation();applyForOpportunity('${opportunity.id}')">Apply Now</button> <button class="btn-secondary" onclick="event.stopPropagation();saveOpportunity('${opportunity.id}')">Save</button> </div> `;return card;}function getTypeName(type){const typeNames={shelter: 'Animal Shelter',conservation: 'Conservation',rescue: 'Rescue',education: 'Education'};return typeNames[type] || type;}function loadMoreOpportunities(allOpportunities){displayedOpportunities+=12;displayOpportunities(allOpportunities);}function updateResultsCount(count){const resultsCount=document.getElementById('results-count');resultsCount.textContent=`Showing ${Math.min(count,displayedOpportunities)}of ${count}opportunities`;}function loadOpportunities(){displayOpportunities(opportunitiesData);}function viewOpportunity(opportunityId){const opportunity=opportunitiesData.find(o=> o.id===opportunityId);if(!opportunity)return;const modal=document.getElementById('opportunity-modal');const modalTitle=document.getElementById('modal-title');const modalBody=document.getElementById('modal-body');modalTitle.textContent=opportunity.title;modalBody.innerHTML=opportunity.content;modal.style.display='flex';document.body.style.overflow='hidden';// Track view trackOpportunityView(opportunity);}function closeOpportunityModal(){const modal=document.getElementById('opportunity-modal');modal.style.display='none';document.body.style.overflow='auto';}function setupModal(){// Close modal when clicking outside document.getElementById('opportunity-modal').addEventListener('click',function(e){if(e.target===this){closeOpportunityModal();}});// Close modal on escape key document.addEventListener('keydown',function(e){if(e.key==='Escape'){closeOpportunityModal();}});}function applyForOpportunity(opportunityId){const opportunity=opportunitiesData.find(o=> o.id===opportunityId);if(!opportunity)return;// Simulate application process showNotification(`Application started for: ${opportunity.title}`,'success');// In a real application,this would open an application form or redirect to application page setTimeout(()=>{showNotification('Application form would open here in a real implementation.','info');},1000);}function saveOpportunity(opportunityId){const opportunity=opportunitiesData.find(o=> o.id===opportunityId);if(!opportunity)return;// Get saved opportunities from localStorage let savedOpportunities=JSON.parse(localStorage.getItem('savedOpportunities')|| '[]');// Check if already saved if(savedOpportunities.includes(opportunityId)){showNotification('Opportunity already saved!','info');return;}// Add to saved savedOpportunities.push(opportunityId);localStorage.setItem('savedOpportunities',JSON.stringify(savedOpportunities));showNotification(`Saved: ${opportunity.title}`,'success');}function trackOpportunityView(opportunity){let viewedOpportunities=JSON.parse(localStorage.getItem('viewedOpportunities')|| '[]');// Remove if already exists viewedOpportunities=viewedOpportunities.filter(item=> item.id !==opportunity.id);// Add to beginning viewedOpportunities.unshift({id: opportunity.id,title: opportunity.title,timestamp: new Date().toISOString()});// Keep only last 10 viewedOpportunities=viewedOpportunities.slice(0,10);localStorage.setItem('viewedOpportunities',JSON.stringify(viewedOpportunities));}function setupNewsletter(){// Newsletter subscription functionality window.subscribeNewsletter=function(){const email=document.getElementById('newsletter-email').value;const location=document.getElementById('newsletter-location').value;if(!email){showNotification('Please enter your email address','error');return;}if(!isValidEmail(email)){showNotification('Please enter a valid email address','error');return;}// Simulate subscription showNotification('Thank you for subscribing! You\'ll receive volunteer opportunities in your area.','success');document.getElementById('newsletter-email').value='';document.getElementById('newsletter-location').value='';};}function isValidEmail(email){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return emailRegex.test(email);}function loadUserLocation(){// Try to get user's location for better filtering if(navigator.geolocation){navigator.geolocation.getCurrentPosition(function(position){// In a real application,you would reverse geocode to get city/state console.log('User location available:',position.coords);},function(error){console.log('Location access denied or unavailable');});}}function showNotification(message,type){// Create notification element const notification=document.createElement('div');notification.className=`notification notification-${type}`;notification.innerHTML=` <i class="fas fa-${type==='success' ? 'check-circle' : type==='error' ? 'exclamation-circle' : 'info-circle'}"></i> <span>${message}</span> `;// Add to page document.body.appendChild(notification);// Show notification setTimeout(()=> notification.classList.add('show'),100);// Hide and remove after 3 seconds setTimeout(()=>{notification.classList.remove('show');setTimeout(()=> document.body.removeChild(notification),300);},3000);}// Add notification styles dynamically const notificationStyles=` .notification{position: fixed;top: 20px;right: 20px;background: white;border-radius: 8px;padding: 1rem 1.5rem;box-shadow: 0 4px 20px rgba(0,0,0,0.15);display: flex;align-items: center;gap: 0.5rem;z-index: 1001;transform: translateX(100%);transition: transform 0.3s ease;max-width: 400px;}.notification.show{transform: translateX(0);}.notification-success{border-left: 4px solid #4CAF50;}.notification-success i{color: #4CAF50;}.notification-error{border-left: 4px solid #F44336;}.notification-error i{color: #F44336;}.notification-info{border-left: 4px solid #2196F3;}.notification-info i{color: #2196F3;}.urgent-badge{background: #F44336;color: white;padding: 0.2rem 0.5rem;border-radius: 4px;font-size: 0.7rem;font-weight: 500;margin-left: 0.5rem;}`;const styleSheet=document.createElement('style');styleSheet.textContent=notificationStyles;document.head.appendChild(styleSheet);